delete from mysql.user where host="localhost" and user="root";
flush privileges;
select host,user,plugin,authentication_string from mysql.user;


DROP SCHEMA IF EXISTS `SlaytheSpireDatabase` ;

CREATE SCHEMA IF NOT EXISTS `SlaytheSpireDatabase` 
  DEFAULT CHARACTER SET utf8 ;
  DEFAULT COLLATE utf8_general_ci;
  
USE `SlaytheSpireDatabase` ;

CREATE TABLE IF NOT EXISTS `SlaytheSpireDatabase`.`hero` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(45) NOT NULL,
  `hp` INT NOT NULL,
  `flavor_text` VARCHAR(255) NOT NULL,
  `image` VARCHAR(2083) NULL,
  PRIMARY KEY (`id`))
ENGINE = InnoDB
    DEFAULT CHARACTER SET = utf8;
    DEFAULT COLLATE utf8_general_ci;

CREATE TABLE IF NOT EXISTS `SlaytheSpireDatabase`.`card` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(45) NOT NULL,
  `cost` VARCHAR(5) NOT NULL,
  `reinforcement` VARCHAR(100) NOT NULL,
  `class` VARCHAR(45) NOT NULL,
  `dedicated` VARCHAR(45) NOT NULL,
  `type` VARCHAR(10) NOT NULL,
  `effect` VARCHAR(200) NULL,
  `comments` VARCHAR(500) NULL,
  `image` VARCHAR(2083) NULL,
  PRIMARY KEY (`id`))
ENGINE = InnoDB
    DEFAULT CHARACTER SET = utf8;
    DEFAULT COLLATE utf8_general_ci;

CREATE TABLE IF NOT EXISTS `SlaytheSpireDatabase`.`start_card` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `card_id` INT NOT NULL,
  `hero_id` INT NOT NULL,
  PRIMARY KEY (`id`),
    FOREIGN KEY (`hero_id`) REFERENCES `SlaytheSpireDatabase`.`hero` (`id`) 
    on update cascade 
    on delete cascade,
    FOREIGN KEY (`card_id`) REFERENCES `SlaytheSpireDatabase`.`card` (`id`) 
    on update cascade 
    on delete cascade
)
ENGINE = InnoDB
    DEFAULT CHARACTER SET = utf8;
    DEFAULT COLLATE utf8_general_ci;

CREATE TABLE IF NOT EXISTS `SlaytheSpireDatabase`.`item` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(45) NOT NULL,
  `class` VARCHAR(45) NOT NULL,
  `dedicated` VARCHAR(45) NOT NULL,
  `effect` VARCHAR(200) NULL,
  `flavor_text` VARCHAR(255) NULL,
  `image` VARCHAR(2083) NULL,
  PRIMARY KEY (`id`))
ENGINE = InnoDB
    DEFAULT CHARACTER SET = utf8;
    DEFAULT COLLATE utf8_general_ci;

CREATE TABLE IF NOT EXISTS `SlaytheSpireDatabase`.`start_item` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `item_id` INT NOT NULL,
  `hero_id` INT NOT NULL,
  PRIMARY KEY (`id`),
    FOREIGN KEY (`hero_id`) REFERENCES `SlaytheSpireDatabase`.`hero` (`id`) 
    on update cascade 
    on delete cascade,
    FOREIGN KEY (`item_id`) REFERENCES `SlaytheSpireDatabase`.`item` (`id`) 
    on update cascade 
    on delete cascade
)
ENGINE = InnoDB
    DEFAULT CHARACTER SET = utf8;
    DEFAULT COLLATE utf8_general_ci;

CREATE TABLE IF NOT EXISTS `SlaytheSpireDatabase`.`potion` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(45) NOT NULL,
  `class` VARCHAR(45) NOT NULL,
  `dedicated` VARCHAR(45) NOT NULL,
  `effect` VARCHAR(255) NULL,
  `image` VARCHAR(2083) NULL,
  PRIMARY KEY (`id`))
ENGINE = InnoDB
    DEFAULT CHARACTER SET = utf8;
    DEFAULT COLLATE utf8_general_ci;







