INSERT INTO hero (id,name,hp,flavor_text,image) VALUES
(null, '아이언클레드', 80, '아이언클래드의 살아남은 병사입니다.악마의 힘을 사용하기 위해 영혼을 팔았습니다.','Ironclad.JPG'),
(null, '사일런트', 70, '안개 지대에서 온 치명적인 사냥꾼입니다.단검과 독으로 적들을 박멸합니다.','Cilent.JPG'),
(null, '디펙트', 75, '자아를 깨달은 전투 자동인형입니다.고대의 기술로 구체를 만들 수 있습니다.','Defect.JPG'),
(null, '와쳐', 72, '첨탑을 "평가"하기 위하여 찾아온 눈먼 수도사입니다.강림의 경지에 이른 고수입니다.','Watcher.JPG');
INSERT INTO card (id,name,cost,reinforcement,class,dedicated,type,effect,comments,image) VALUES
(null,'타격','1','피해(6→9)','일반','Ironclad','공격','피해를 6 줍니다.','아이언클래드의 기본카드','Strike_Ironclad.JPG'),
(null,'강타','2','피해(8→10), 취약(2→3)','일반','Ironclad','공격','피해를 8 줍니다.취약을 2 부여합니다.','아이언클래드의 기본카드. 취약 디버프를 건다.','Bash.JPG'),
(null,'분노','0','피해(6→8)','일반','Ironclad','공격','피해를 6 줍니다.이 카드를 1장 복사해 버린 카드 더미에 섞어 넣습니다.','0코스트 증식카드','Anger.JPG'),
(null,'몸통 박치기','0','비용(1→0)','일반','Ironclad','공격','현재 방어도만큼 피해를 줍니다.','방밀덱의 핵심카드','Body Slam.JPG'),
(null,'격돌','0','피해(14→18)','일반','Ironclad','공격','손에 있는 카드가 전부 공격 카드일 때만 사용할 수 있습니다.피해를 14 줍니다.','0코스트 고데미지카드. 제약이 심하다.','Clash.JPG'),
(null,'절단','1','피해(8→11)','일반','Ironclad','공격','적 전체에게 피해를 8 줍니다.','무난한 광역기카드','Cleave.JPG'),
(null,'클로스라인','2','피해(12→14), 약화(2→3)','일반','Ironclad','공격','피해를 12 줍니다.약화 2를 부여합니다.','준수한 데미지에 약화가 있는 카드 하지만 코스트가 무거워서 잘 쓰기 힘들다.','Clothesline.JPG'),
(null,'박치기','1','피해(9→12)','일반','Ironclad','공격','피해를 9 줍니다.버린 카드 더미에서 카드 1장을 선택해 뽑을 카드 더미의 맨 위에 놓습니다.','다음턴 설계하기에 좋은 카드. 드로우와 연계해서 확정드로우로 사용가능하다.','Headbutt.JPG'),
(null, '대검','2','힘효과(3→5)', '일반', 'Ironclad', '공격','피해를 14 줍니다.힘의 효과가 3배로 적용됩니다.', '힘이 있을 때 높은 위력을 발휘하는 카드 하지만 코스트가 무겁다.', 'Heavy Blade.JPG'),
(null, '철의 파동','1','피해(5→7), 방어(5→7)', '일반', 'Ironclad', '공격','방어도를 5 얻습니다.피해를 5 줍니다.', '공방을 겸하는 카드', 'Iron Wave.JPG'),
(null, '완벽한 타격','2','타격카드로 증가하는 피해량(2→3)', '일반', 'Ironclad', '공격',"피해를 6 줍니다.보유 중인 카드 중 이름에 '타격'이 포함된 카드 하나당 피해량이 2 증가합니다.", '완벽한 타격덱의 필수카드, 폼멜타격/이중타격/난폭한타격/완벽한타격 등을 많이 집어서 피해량을 증식시키는 카드', 'Perfected Strike.JPG'),
(null, '폼멜 타격','1','피해(9→10), 뽑는카드(1장→2장)', '일반', 'Ironclad', '공격','피해를 9 줍니다.카드를 1장 뽑습니다.', '공격 겸 드로우카드', 'Pommel Strike.JPG'),
(null, '부메랑 칼날','1','반복 횟수(3→4)', '일반', 'Ironclad', '공격','무작위 적들에게 피해를 3 만큼 3번 줍니다.', '힘을 올려주는 카드와 시너지가 나는 카드', 'Sword Boomerang.JPG'),
(null, '천둥','1','피해(4→7)', '일반', 'Ironclad', '공격','적 전체에게 피해를 4 주고 취약 1을 부여합니다.', '광역피해 및 광역디버프. 코스트도 낮다.', 'Thunderclap.JPG'),
(null, '이중 타격','1','피해(5→7)', '일반', 'Ironclad', '공격','피해를 5 만큼 2번 줍니다.', '초반에 차용하기에 좋은 준수한 카드. 힘덱으로 빌드업하기에도 좋다.', 'Twin Strike.JPG'),
(null, '난폭한 타격','1','피해(12→17)', '일반', 'Ironclad', '공격','피해를 12 줍니다.뽑을 카드 더미에 부상 카드를 섞어 넣습니다.', '높은피해량. 하지만 카드더미에 부상이 넣어지는 패널티가 있다.', 'Wild Strike.JPG'),
(null, '피에는 피','4','피해(18→22), 비용(4→3)', '특별', 'Ironclad', '공격','이번 전투에서 체력을 잃을 때마다 이 카드의 비용이 1 감소합니다.피해를 18 줍니다.', '막대한 피해량과 코스트 하지만 전투중에 체력을 잃는 상황은 자주있는 상황이라 생각보다 크게 비용이 부담되지는 않는다.', 'Blood for Blood.JPG'),
(null, '대학살','2','피해(20→28)', '특별', 'Ironclad', '공격','피해를 20 줍니다.(휘발성)', '강력한 피해량. 후반에도 휘발성덕분에 사라지므로 크게 부담되지는 않는다. 코스트가 무겁다.', 'Carnage.JPG'),
(null, '드롭킥','1','피해(5→8)', '특별', 'Ironclad', '공격','피해를 5 줍니다.적이 취약 상태라면 에너지 1개를 얻고 카드를 1장 뽑습니다.', '드롭킥 순환덱의 키 카드.', 'Dropkick.JPG'),
(null, '혈류','1','피해(15→20)', '특별', 'Ironclad', '공격','체력을 2 잃습니다.피해를 15 줍니다.', '강력한 피해량.강화량도 좋지만 피해량이 커서 자해덱 등 상황에 맞게 써야한다.', 'Hemokinesis.JPG'),
(null, '연타','1','공격횟수(4→5)', '특별', 'Ironclad','공격', '피해를 2 만큼 4번 줍니다.(소멸)', '다단히트카드로 힘을 올려주는 카드와 시너지가 나는 카드. 저코스트에 소멸이라 가볍게 쓸만하다.', 'Carnage.JPG'),
(null, '광란','1','증가하는 피해량(5→8)', '특별', 'Ironclad', '공격','피해를 8 줍니다.이 카드를 사용할 때마다 이번 전투 동안 이 카드의 피해량이 5 증가합니다.','덱압축만 한다면 주력으로 사용하기에 나쁘지 않은 카드.초반벨류도 나쁘지 않다.', 'Rampage.JPG'),
(null, '불태우는 일격','2','피해(12+n(n+7)/2)', '특별', 'Ironclad', '공격','피해를 12 줍니다.강화 횟수에 제한이 없습니다.', '강화효율이 기하급수적으로 증가하는 특이한 카드. 초반에 얻는다면 엄청난 효과를 누릴수있다.', 'Searing Blow.JPG'),
(null, '영혼 절단','2','피해(16→22)', '특별', 'Ironclad', '공격','손에 있는 공격 카드를 제외한 카드를 모두 소멸시킵니다.피해를 16 줍니다.','소멸과 공격을 동시에하는 카드. 소멸시너지가 급할때 차용하기 좋은 카드.', 'Sever Soul.JPG'),
(null, '어퍼컷','2','취약(1→2), 약화(1→2)', '특별', 'Ironclad', '공격','피해를 13 줍니다.취약을 1 부여합니다.약화를 1 부여합니다.','공격도 하면서 약화와취약을 한장의 카드로 걸 수 있게해주는 카드. 강화의존도가 높다.', 'Uppercut.JPG'),
(null, '소용돌이','X','피해(5→8)', '특별', 'Ironclad', '공격','적 전체에게 피해를 5 만큼 X번 줍니다.','무난한 광역기. 기본공격력이 매우 낮은 단점이 있다. 여러 조건을 만족시킬수만 있다면 효율이 극대화할 수 있다.', 'Whirlwind.JPG'),
(null, '무모한 돌진','0','피해(7→10)', '특별', 'Ironclad', '공격','피해를 7 줍니다.뽑을 카드 더미에 어지러움 카드를 섞어 넣습니다.','0코스트에 준수한 데미지. 그만큼 어지러움이라는 패널티가 있다.', 'Reckless Charge.JPG'),
(null, '몽둥이질','3','피해(32→42)', '희귀', 'Ironclad', '공격','피해를 32 줍니다.','엄청난 깡딜. 그만큼 코스트가 가장 무겁다. 초반에 집는다면 왠만한 1막과 2막의 엘리트몬스터을 포함한 모든 몬스터들은 잡을 수 있다.하지만 후반에는 가성비가 급격히 안좋아지는 카드다.', 'Bludgeon.JPG'),
(null, '포식','1','피해(10→12), 최대 체력 증가량(3→4)', '희귀', 'Ironclad', '공격','피해를 10 줍니다.치명타라면 최대 체력이 3 증가합니다.(소멸)','초반에 집을수록 효율이 좋은카드. 최대체력을 증가시킬 수 있는 것이 장점. 3막보스인 도누를 포식으로 막타로 잡아야하는 도전과제도 있다.', 'Feed.JPG'),
(null, '지옥불','2','피해(7→10)', '희귀', 'Ironclad', '공격','카드를 전부 소멸시킵니다.그 수만큼 피해를 7 줍니다.(소멸)','덱압축에 좋은 카드. 특히 유물인 말라 죽은 가지와 시너지가 뛰어나다.', 'Fiend Fire.JPG'),
(null, '화형','2','피해(21→28)', '희귀', 'Ironclad', '공격','적 전체에게 피해를 21 줍니다.버린 카드 더미에 화상 카드를 섞어 넣습니다.','높은 광역딜. 화상을 얻는다는 패널티가 존재한다.', 'Immolate.JPG'),
(null, '사신','2','피해(4→5)', '희귀', 'Ironclad', '공격','적 전체에게 피해를 4 줍니다.막히지 않은 피해만큼 체력을 회복합니다.(소멸)','흡혈카드. 체력회복인데 광역딜까지겸하는 중요한카드.', 'Reaper.JPG'),
(null, '수비','1','방어도(5→8)', '일반', 'Ironclad', '스킬','방어도를 5 얻습니다.','기본방어카드.스킬카드라 소멸로 소멸시키기 쉬워서 덱압축우선순위에서 타격보다 낮다.', 'Defend_Ironclad.JPG'),
(null, '전투장비','1','카드1장→패에 있는 모든카드', '일반', 'Ironclad', '스킬','방어도를 5 얻습니다.카드 1장을 전투가 끝날 때까지 강화합니다.','신성카드의 하위호환이지만 일반카드라 구하기쉬운점을 생각하면 집기편하다는 장점이 있다.', 'Armaments.JPG'),
(null, '몸 풀기','0','힘(2 → 4), 잃는 힘 (2→4)', '일반', 'Ironclad', '스킬','힘을 2 얻습니다.턴이 끝날 때 힘을 2 잃습니다.','비용이 0이라서 공격카드와 연계하기가 좋다.인공물을 얻어서 힘을 잃는 디버프를 막을 수도 있다.', 'Flex.JPG'),
(null, '파괴','1','비용(1→0)', '일반', 'Ironclad', '스킬','뽑을 카드 더미 맨 위의 카드를 사용한 뒤 소멸시킵니다.','강화의존도가 높은카드. 무거운덱에서 사용하기 좋다.', 'Havoc.JPG'),
(null, '흘려보내기','1','방어도(8→11)', '일반', 'Ironclad', '스킬','방어도를 8 얻습니다.카드를 1장 뽑습니다.','방어와 드로우를 겸한카드 심지어 기본방어카드보다도 방어도가 더 좋다. 1코스트에 방어도도좋고 드로우까지되는 사기카드 중 하나', 'Shrug It Off.JPG'),
(null, '진정한 끈기','1','방어(7→9), 무작위카드소멸→선택한 카드소멸', '일반', 'Ironclad', '스킬','방어도를 7 얻습니다.무작위 카드를 1장 소멸시킵니다.','랜덤성때문에 강화의존도가 매우 높은편. 하지만 저주나 다양한 디버프카드들을 제거하기에 매우 좋은 카드 중 하나이다.', 'True Grit.JPG'),
(null, '전투의 함성','0','뽑는 카드(1장→2장)', '일반', 'Ironclad', '스킬','카드를 1장 뽑습니다.카드 1장을 뽑을 카드 더미 맨 위에 놓습니다.(소멸)','강화를 해야 드로우손해를 안보는 카드라는점에서 강화의존도가 높다. 패가 꼬였을 때 정비하게 해준다는점에서 좋은카드', 'Warcry.JPG'),
(null, '전투 최면','0','뽑는카드(3장→4장)', '특별', 'Ironclad', '스킬','카드를 3장 뽑습니다.이번 턴 동안 더 이상 카드를 뽑을 수 없습니다.','노코스트의 쓰기편한 드로우카드.', 'Battle Trance.JPG'),
(null, '이도류','1','복사량(1장→2장)', '특별', 'Ironclad', '스킬','공격 또는 파워 카드를 복사합니다.','다양한 응용이 가능한 카드. 여러 카드들과 시너지를 내기에 좋다.', 'Dual Wield.JPG'),
(null, '사혈','0','얻는 코스트(2개→3개)', '특별', 'Ironclad', '스킬','코스트 2개를 얻습니다.체력을 3 잃습니다.','체력과 코스트를 교환하는 카드. 자주 쓰기에는 체력이 없어져서 애매하다.', 'Bloodletting.JPG'),
(null, '불타는 조약','1','뽑는카드(2장→3장)', '특별', 'Ironclad', '스킬','카드를 1장 소멸시킵니다.카드를 2장 뽑습니다.','덱압축과 드로우를 동시에 해주는 카드.강화전에는 드로우이득도 없는데 코스트만 잡아먹어서 집을 때 잘 생각해야한다. 그럼에도 불구하고 소멸덱이나 여러카들과의 시너지도 좋은편이다.', 'Burning Pact.JPG'),
(null, '무장 해제','1','힘 감소량(2→3)', '특별', 'Ironclad', '스킬','적의 힘을 2 감소시킵니다.(소멸)','아이언클래드의 유일한 힘감소카드. 영구적효과라는 점에서 좋다.', 'Disarm.JPG'),
(null, '참호','2','비용(2→1)', '특별', 'Ironclad', '스킬','방어도가 2배로 증가합니다.','방밀덱에 활용되는 카드. 일정조건하에서는 엄청난 밸류를 가지고 있는 카드. 코스트가 줄어든다는 점에서 강화의존도가 매우 높은편이다.', 'Entrench.JPG'),
(null, '화염 장벽','2','방어(12→16), 피해(4→6)', '특별', 'Ironclad', '스킬','방어도를 12 얻습니다.이번 턴에 공격을 받을 때마다 공격한 대상에게 피해를 4 줍니다.','방어와 동시에 다단히트계열 몬스터들에게 효과적인 카드. 특히 심장에서 쓴다면 매우 좋다.', 'Flame Barrier.JPG'),
(null, '유령갑옷','1','방어(10→13)', '특별', 'Ironclad', '스킬','방어도를 10 얻습니다.(휘발성)','코스트대비 높은 방어도.패널티도 낮은편이라 무난히 사용하기 좋다.', 'Ghostly Armor.JPG'),
(null, '지옥검','1','비용(1→0)', '특별', 'Ironclad', '스킬','무작위 공격 카드를 1장 얻습니다.이번 턴 동안 그 카드의 비용이 0이 됩니다.(소멸)','이번턴에만 0인것에 주의해야한다. 초반의 부족한 화력을 보충하는데 좋다.', 'Infernal Blade.JPG'),
(null, '위압','0','약화(1→2)', '특별', 'Ironclad', '스킬','적 전체에게 약화를 1 부여합니다.(소멸)','덱에 전체적으로 방어가 낮을때 차용하기 좋다. 소멸덱이라면 매우 좋지만 아니라면 애매한카드.', 'Intimidate.JPG'),
(null, '난관 극복','1','방어(15→20)', '특별', 'Ironclad', '스킬','부상 카드를 2장 받습니다.방어도를 15 얻습니다.','높은 패널티만큼 높은방어와 낮은 코스트. 부상을 해결할 수만 있다면 매우 좋은카드.', 'Power Through.JPG'),
(null, '격노','0','방어(3→5)', '특별', 'Ironclad', '스킬','이번 턴에 공격 카드를 사용할 때마다 방어도를 3 얻습니다.','노코스트라 매력적인 카드. 1장정도라면 고려해볼만하다.', 'Rage.JPG'),
(null, '기사회생','1','방어(5→7)', '특별', 'Ironclad', '스킬','손에 있는 공격 카드를 제외한 모든 카드를 소멸시키고 그 수만큼 방어도를 5 얻습니다.','매우 좋은 성능의 카드. 저주나 상태이상카드들을 지워주면서 방어까지 얻는데 코스트도 낮은 매우 좋은 카드다.심지어 덱압축효과도 있다.', 'Second Wind.JPG'),
(null, '혈안','0','비용(1→0)', '특별', 'Ironclad', '스킬','코스트 2개를 얻습니다.(소멸)','추가코스트를 제공해주는 좋은카드 고코스트덱이나 드로우가 많은덱과 시너지가 좋다.', 'Seeing Red.JPG'),
(null, '감시병','1','방어(5→8), 얻는 코스트(2→3)', '특별', 'Ironclad', '스킬','방어도를 5 얻습니다.이 카드가 소멸되면, 코스트 2개를 얻습니다.','소멸이없으면 수비카드와 다를것이 없는 카드 하지만 소멸만있다면 충분한 성능을 보장한다.', 'Sentinel.JPG'),
(null, '충격파','2','취약(3→5), 약화(3→5)', '특별', 'Ironclad', '스킬','적 전체에게 약화와 취약을 3 부여합니다.(소멸)','무난하게 강력한 카드. 두개의 디버프가 높은수치로 광역으로 걸 수 있다는 점이 장점.', 'Shockwave.JPG'),
(null, '약점 분석','1','힘(3→4)', '특별', 'Ironclad', '스킬','선택한 적이 공격할 의도가 있다면 힘을 3 얻습니다.','파워카드인 발화보다 힘증가량도 높고 여러번 사용가능하지만 상대가 공격할때만 사용가능하다는 조건때문에 타이밍이 엇갈리면 오히려 드로우만 손해보는 카드.', 'Spot Weaknes.JPG'),
(null, '연사','1','다음 공격카드(1장→2장)', '희귀', 'Ironclad', '스킬','이번 턴 동안 다음에 사용하는 공격 카드가 두 번 사용됩니다.','2코스트 이상의 딜카드가 주력인 덱에서 차용하기 좋다. 코스트이득을 많이볼 수 있는 카드. 하지만 여유코스트가 없다면 강화효과를 보기는 어렵다.', 'Double Tap.JPG'),
(null, '발굴','1','비용(1→0)', '희귀', 'Ironclad', '스킬','소멸된 카드를 1장 선택해 가져옵니다.(소멸)','소멸카드는 대부분 1회용이지만 높은 성능을 자랑한다.그렇기 때문에 이 카드는 꽤 좋은 성능을 자랑하고, 소멸덱의 경우 상황에 맞는 카드를 골라서 가져올 수도 있다.', 'Exhume.JPG'),
(null, '무적','2','방어(30→40)', '희귀', 'Ironclad', '스킬','방어도를 30 얻습니다.(소멸)','엄청난 방어도를 자랑하는 카드. 한장만으로 한 턴을 버티게해준다. 참호와 연계되면 두배가 되므로 60 or 80의 방어도를 얻을 수 있다. 이것을 방밀을 쓰면 한턴에 60 or 80딜이라는 괴랄한 수치가 나온다.', 'Impervious.JPG'),
(null, '한계 돌파','1','소멸하지 않음', '희귀', 'Ironclad', '스킬','힘이 2배로 증가합니다.(소멸)','힘 덱의 주요 키카드. 곱연산이라서 매우 빠르게 증가하는게 포인트', 'Limit Break.JPG'),
(null, '제물','0','뽑는카드(3→5)', '희귀', 'Ironclad', '스킬','체력을 6 잃습니다.에너지 2개를 얻습니다.카드를 3장 뽑습니다.(소멸)','아이언클래드의 대표적 사기카드. 코스트와 드로우를 동시에 얻을 수 있는 희대의 사기카드.', 'Offering.JPG'),
(null, '연소','1','피해(5→7)', '특별', 'Ironclad', '파워','턴 종료시 체력을 1 잃고 적 전체에게 피해를 5 줍니다.','매턴 체력이 깎이는 만큼 애매한 카드. 하지만 광역기가 필요한 초반보스 특히 슬라임의 경우 효과가 좋다.', 'Combust.JPG'),
(null, '진화','1','뽑는카드(1장→2장)', '특별', 'Ironclad', '파워','상태이상 카드를 뽑으면 카드를 1장 뽑습니다.','강화하면 상태이상이 드로우카드가 되어버리는 카드. 특히 아이언클래드는 패널티를 가진 카드가 많아서 더더욱 좋다.', 'Evolve.JPG'),
(null, '무감각','1','방어도(3→4)', '특별', 'Ironclad', '파워','카드가 소멸될 때마다 방어도를 3 얻습니다.','대표적인 사기카드. 소멸이많은 아이언클래드에게 방어를 챙기게 해주는 카드', 'Feel No Pain.JPG'),
(null, '불 뿜기','1','피해(6→10)', '특별', 'Ironclad', '파워','상태이상이나 저주 카드를 뽑을 경우 적 전체에게 피해를 6 줍니다.','부상덱에서 쓰일만한 키카드.특히 상태이상에 의존하는 몬스터들에게 카운터카드이다.', 'Fire Breathing.JPG'),
(null, '발화','1','힘(2→3)', '특별', 'Ironclad', '파워','힘을 2 얻습니다.','저코스트에 영구적으로 힘을 올려주는 무난한 카드', 'Inflame.JPG'),
(null, '금속화','1','방어(3→4)', '특별', 'Ironclad', '파워','턴 종료시 방어도를 3 얻습니다.','얻을 수 있는 방어도가 매우 낮아서 그렇게 좋지는 않다. 초반에 체력관리가 안될때는 한장정도는 고려해볼만하다.', 'Metallicize.JPG'),
(null, '파열','1','힘(1→2)', '특별', 'Ironclad', '파워','카드의 효과로 체력을 잃을 때마다, 힘을 1 얻습니다.','자해덱에서 사용할 수 있는 카드. 잔혹성과 연계하면 유사 악마화로 사용가능하다. ', 'Rupture.JPG'),
(null, '어둠의 포옹','2','비용(2→1)', '특별', 'Ironclad', '파워','카드가 소멸될 때마다 1장의 카드를 뽑습니다.','소멸덱 회전의 중요 키카드. 이 카드하나로 사이클이 완성될 수 있는 강력한 카드', 'Dark Embrace.JPG'),
(null, '바리케이드','3','비용(3→2)', '희귀', 'Ironclad', '파워','턴 시작시 방어도가 사라지지 않습니다.','방밀덱의 필수카드. 방어도가 사라지지않고 영구지속되는 카드. 그만큼 코스트가 매우 무거워 잘못쓰면 방어도 쌓을려다 얻어맞고 죽는경우가 많다.', 'Barricade.JPG'),
(null, '광폭화','0','취약(2→1)', '희귀', 'Ironclad', '파워','취약을 2 받습니다.매 턴마다 에너지 1개를 추가로 얻습니다.','코스트를 늘려주는 좋은카드. 강화시 디버프를 줄여주는 효과가 있어서 강화의존도가 높은편이다.', 'Berserk.JPG'),
(null, '잔혹성','0','선천성부여', '희귀', 'Ironclad', '파워','턴 시작 시 체력을 1 잃고 카드를 1장 뽑습니다.','적은리스크로 추가드로우를 보게 해주지만 체력관리가 어렵게된다는 단점이 있다.', 'Brutality.JPG'),
(null, '타락','3','비용(3→2)', '희귀', 'Ironclad', '파워','스킬 카드의 비용이 0이 됩니다.스킬 카드를 사용할 때마다 소멸시킵니다.','소멸덱의 핵심카드. 말라죽은 가지와 엄청난 시너지 효과가 있다. 가지타락덱은 이 게임의 모든 덱에서 손꼽는 사기덱이다.', 'Corruption.JPG'),
(null, '악마의 형상','3','힘(2→3)', '희귀', 'Ironclad', '파워','매 턴 시작시 힘을 2 얻습니다.','호불호가 갈리는 카드. 한번쓰면 가면갈수록 무조건 쎄지는 카드지만 코스트가 너무 무거워서 단점이 큰 편이다.', 'Demon Form.JPG'),
(null, '절대적인 힘','2','피해(5→7)', '희귀', 'Ironclad', '파워','방어도를 얻을 때마다 무작위 적에게 5의 피해를 줍니다.','무거운 코스트대비 낮은 리턴으로 잘 안쓰이지만 방밀덱에서 몸통박치기를 못 찾았을 때 차선으로 사용하기 좋다.', 'Juggernaut.JPG'),
(null, '타격','1','피해(6→9)', '일반', 'Silent', '공격','적 하나에게 6의 피해를 입힙니다.','사일런트의 기본공격카드. 성능이 매우 안좋아서 덱압축해야할 최우선순위이다.', 'Strike_Silent.JPG'),
(null, '무력화','0','피해(3→4), 약화(1→2)', '일반', 'Silent','공격', '적 하나에게 3의 피해를 입히고 약화 1을 부여합니다.','기본카드지만 좋은 성능을 발휘하는 카드.특히 강화효율이 엄청 좋은편이다.', 'Neutralize.JPG'),
(null, '파멸','1','피해(7→10)', '일반', 'Silent', '공격', '적 하나에게 7의 피해를 입히고 해당 적이 중독 상태이면 7의 피해를 한 번 더 입힙니다.','독덱에서 사용하기 좋은 카드. 단기간 극딜을 넣을 수 있는 카드.', 'Bane.JPG'),
(null, '단검 분사','1','피해(4→6)', '일반', 'Silent', '공격', '모든 적에게 4의 피해를 2번 입힙니다.','초반에 엄청 유용한 카드. 광역카드. ', 'Dagger Spray.JPG'),
(null, '단검 투척','1','피해(9→12)', '일반', 'Silent', '공격', '적 하나에게 9의 피해를 입히고 카드를 1장 뽑고 1장 버립니다.','덱순환과 준수한 코스트대비 깡딜.', 'Dagger Throw.JPG'),
(null, '플라잉 니킥','1','피해(8→11)', '일반', 'Silent', '공격', '적 하나에게 8의 피해를 입히고 다음 턴에 코스트 1개를 얻습니다.','자체 피해량이 낮은편이고 즉시 이득을 주지 않아 미묘한 카드.', 'Flying Knee.JPG'),
(null, '독 찌르기','1','피해(6→8), 중독(3→4)', '일반', 'Silent', '공격', '적 하나에게 6의 피해를 입히고 중독 3을 부여합니다.','중독딜까지 포함하면 1코스트카드중에서 손꼽히는 딜을 자랑한다.', 'Poisoned Stab.JPG'),
(null, '전광석화','1','피해(8→12)', '일반', 'Silent', '공격', '적 하나에게 8의 피해를 입히고 카드를 1장 뽑습니다.','준수한 딜과 드로우카드. 하지만 폼멜타격의 하위호환격에 시너지도 보기가 어렵다.', 'Quick Slash.JPG'),
(null, '칼질','0','피해(6→9)', '일반', 'Silent', '공격', '적 하나에게 6의 피해를 입힙니다.','노코스트의 낮은 피해. 효율이 낮지만 초반에 딜이 부족한경우 채용하는 경우가 있다.', 'Slice.JPG'),
(null, '불의의 일격','1','피해(7→9), 약화(1→2)', '일반', 'Silent', '공격', '적 하나에게 7의 피해를 입히고 약화 1을 부여합니다.','크로스라인의 1코스트버전. 이미 무력화라는 약화카드가 있어서 채용하기가 애매하다.', 'Sucker Punch.JPG'),
(null, '비열한 타격','2','피해(12→16)', '일반', 'Silent', '공격', '12의 피해를 줍니다. 이번 턴에 버린 카드가 있으면 에너지 2를 얻습니다.','버리기와 연계해 노코스트로 준수한 딜을 줄 수 있다.', 'Sneaky Strike.JPG'),
(null, '총공격','1','피해(10→14)', '특별', 'Silent', '공격', '모든 적에게 10의 피해를 입힙니다. 무작위 카드를 1장 버립니다.','높은 광역딜링. 하지만 카드를 버린다는 패널티에 주의해야한다.', 'All-Out Attack.JPG'),
(null, '배신','0','피해(11→15)', '특별', 'Silent', '공격', '적 하나에게 11의 피해를 입힙니다. 선천성.(소멸)','높은 딜링에 노코스트라는 강력한 카드. 심지어 소멸이라 패널티도 적다.', 'Backstab.JPG'),
(null, '교살','2','추가피해(3→5)', '특별', 'Silent', '공격', '적 하나에게 12의 피해를 입힙니다. 이번 턴에 카드를 사용할 때마다 해당 적은 3의 피해를 받습니다.','부족한 딜을 채울 수 있는 카드. 특히 적당히 순환이 되는 미라클덱에서는 원턴킬도 노릴만한 카드.', 'Choke.JPG'),
(null, '돌진','2','피해(10→13), 방어(10→13)', '특별', 'Silent', '공격', '적 하나에게 10의 피해를 입힙니다. 방어 10을 얻습니다.','딜과 방어를 동시에하는 카드. 초반에 사용하기가 좋다.', 'Dash.JPG'),
(null, '영원한 고통','0','피해(4→6)', '특별', 'Silent', '공격', '적 하나에게 4의 피해를 입힙니다. 이 카드를 뽑을 때마다 이 카드를 복사하여 손에 가져옵니다.(소멸)','0코스트에 준수한 딜카드라 초반에 사용하기에 너무좋다.특히 소멸이라 덱순환에 큰 영향을 끼치지 않기 때문에 여러모로 쓸만하다.', 'Endless Agony.JPG'),
(null, '내장 해체','4','피해(7→9)', '특별', 'Silent', '공격', '이번 턴에 카드를 버릴 때마다 비용이 1 감소합니다. 적 하나에게 7의 피해를 3번 입힙니다.','미라클덱에서 딜링기로 채택하는 카드. 제대로 연계된 미라클덱에서는 노코스트로 사용가능하지만 애매하게 완성하면 코스트문제때문에 애매할 수 있다.', 'Eviscerate.JPG'),
(null, '마무리','1','피해(6→8)', '특별', 'Silent', '공격', '적 하나에게 이번 턴에 사용한 공격 카드 하나당 6의 피해를 입힙니다.','엄청난 폭딜을 낼 수 있는 카드. 그야말로 마무리하기에 좋은 카드다.', 'Finisher.JPG'),
(null, '프레췌','1','피해(4→6)', '특별', 'Silent', '공격', '적 하나에게 손에 있는 스킬 카드 하나당 4의 피해를 입힙니다.','미라클덱에서 채택하는 주력 딜링수단. 코스트대비 기대값이 매우 높아서 덱순환 도중에 사용하기가 좋다. 특히 드로우가 많은 미라클덱에서 사용하기가 좋다.', 'Flechettes.JPG'),
(null, '다리 꺾기','1','피해(5→8)', '특별', 'Silent', '공격', '적 하나에게 5의 피해를 입힙니다. 해당 적이 약화 효과에 걸린 상태라면 에너지 1개를 얻고 카드를 1장 뽑습니다.','성능은 무난한 편이지만 무한 순환덱을 만들기에는 소멸로 덱압축을 하지않는 사일런트로는 어렵다. 덱압축을 엄청나게 해야한다.', 'Heel Hook.JPG'),
(null, '능숙한 찌르기','0','피해(12→16)', '특별', 'Silent', '공격', '피해를 입을 때마다 비용이 1 상승합니다.','피해를 입을수록 손에서 놀게되는 저주카드가 되어버리는 카드. 초반에는 사용할만하지만 1막을 지나면 지워야 하기때문에 상황을 보면서 집어야 한다.', 'Masterful Stab.JPG'),
(null, '천적','2','피해(15→20)', '특별', 'Silent', '공격', '적 하나에게 15의 피해를 입힙니다. 다음 턴에 카드를 2장 더 뽑습니다.','다음턴을 대비하는 카드.추가2드로우가 생각보다 얻기 힘든 이득이기때문에 1장정도는 채용할만하다.', 'Predator.JPG'),
(null, '벌집','2','피해(3→4)', '특별', 'Silent', '공격', '적 하나에게 3의 피해를 5번 입힙니다.','다단히트 공격.하지만 힘을 펌핑할 수단이 사일런트에겐 없어서 애매한 카드.', 'Riddle with Holes.JPG'),
(null, '꼬챙이','X','피해(7→10)', '특별', 'Silent', '공격', '모든 에너지를 소모합니다. 적 하나에게 7의 피해를 X번 입힙니다.','코스트 자원을 펌핑할 수단이 많아서 생각보다 쓸만한 카드. 특히 유물인 아이스크림과 시너지가 좋다.', 'Skewer.JPG'),
(null, '죽어 죽어 죽어','1','피해(13→17)', '희귀', 'Silent', '공격', '모든 적에게 13의 피해를 입힙니다.(소멸)','엄청 좋은 광역기. 특히 소멸이라 크게 덱메이킹에 방해가 되지 않는게 장점.', 'Die Die Die.JPG'),
(null, '유리칼','1','피해(8→12)', '희귀', 'Silent', '공격', '적 하나에게 8의 피해를 2번 입힙니다. 이 카드를 사용할 때마다 이 전투에서만 피해량이 2만큼 감소합니다.','낮은 코스트에 강력한 폭딜. 디버프는 생각보다 한번의 전투에서 여러번 사용하기 어렵기 때문에 보스전이 아니라면 큰 단점이 되지 않는다.', 'Glass Knife.JPG'),
(null, '대단원의 막','0','(50→60)', '희귀', 'Silent', '공격', '뽑을 카드 더미에 카드가 하나도 없을 때만 사용할 수 있습니다. 모든 적에게 50의 피해를 입힙니다.','0코스트에 50피해라는 강력한카드. 하지만 발동시키기 난해한 조건을 가지고 있다.', 'Grand Finale.JPG'),
(null, '처분','1','피해(14→18)', '희귀', 'Silent', '공격', '적 하나에게 14의 피해를 입힙니다. 손에 들고 있는 카드 중 공격 카드가 아닌 카드들을 모두 버립니다.','저코스트의 상당한 딜량. 하지만 모두 버린다는 조건이 생각보다 잘 쓰이지 않아 선택하기가 애매한 경우가 많다.', 'Unload.JPG'),
(null, '수비','1','방어(5→8)', '일반', 'Silent', '스킬', '방어5를 얻습니다.','발놀림카드때문에 제거 우선순위가 상대적으로 낮지만 초반공격이 매우 약하다는 사일런트의 특성상 오히려 수비를 더 빨리 지우는 경우도 있다.', 'Defend_Silent.JPG'),
(null, '생존자','1','(8→11)', '일반', 'Silent', '스킬', '방어 8을 얻습니다. 카드를 1장 버립니다.','사일런트의 기본카드. 상당히 높은 방어도로 초반을 잘 버티게해준다. 후반에도 버리기카드로 연계가 가능해 잘 안지우는 편이다.', 'Survivor.JPG'),
(null, '곡예','1','뽑는카드(3장→4장)', '일반', 'Silent', '스킬', '카드를 3장 뽑습니다. 카드를 1장 버립니다.','드로우로 덱순환을 가속하면서 버리기로 추가이득도보는 카드.', 'Acrobatics.JPG'),
(null, '공중제비','1','방어(5→8)', '일반', 'Silent', '스킬', '방어 5를 얻습니다. 카드를 2장 뽑습니다.','방어와 드로우를 같이보는 카드. 여러덱에 범용적으로 유용한 카드.', 'Backflip.JPG'),
(null, '맹독','1','(5→7)', '일반', 'Silent', '스킬', '적 하나에게 중독 5를 부여합니다.','독덱의 기본적이면서 강력한 카드.', 'Deadly Poison.JPG'),
(null, '튕겨내기','0','(4→7)', '일반', 'Silent', '스킬', '방어 4를 얻습니다.','노코스트로 방어도를 얻을 수 있는 카드.', 'Deflect.JPG'),
(null, '구르기','1','방어(4→6)', '일반', 'Silent', '스킬', '방어 4를 얻습니다. 다음 턴에 방어 4를 얻습니다.','다음턴에도 방어도를 제공해줘서 민첩의 효과를 두배로 받을 수 있다는 이점이 있고, 흐릿함과 연계시 높은 방어도의 이점을 살릴 수 있다.', 'Dodge and Roll.JPG'),
(null, '압도','1','얻는 코스트(2→3)', '일반', 'Silent','스킬', '다음 턴에 에너지 2개를 추가로 얻습니다.','다음턴을 위한 코스트 펌핑카드.', 'Outmaneuver.JPG'),
(null, '귀를 찢는 비명','1','힘 감소량(6→8)', '일반', 'Silent', '스킬','이번 턴에만 모든 적의 힘을 6만큼 감소시킵니다.(소멸)','매우 좋은 방어카드. 광역힘감소 디버프이다. 특히 심장에서 쓰면 매우 좋은효과를 얻을 수 있다.', 'Piercing Wail.JPG'),
(null, '예비','1','뽑는 카드(1장→2장), 버리는 카드(1장→2장)', '일반', 'Silent', '스킬','카드를 1장 뽑습니다. 카드를 1장 버립니다.','강화를 안할때는 예비자체가 패를 차지하기 때문에 손해를 보는 카드라서 무조건 강화가 필수이다.', 'Prepared.JPG'),
(null, '망토와 단검','1','단도카드(1장→2장)', '일반', 'Silent', '스킬','방어 6을 얻습니다. 단도 카드를 1장 얻습니다.','단도덱의 핵심. 공방 둘다되는 카드.', 'Cloak and Dagger.JPG'),
(null, '검무','1','단도(3장→4장)', '일반', 'Silent', '스킬','단도 카드 3장을 손으로 가져옵니다.','단도덱의 핵심카드.', 'Blade Dance.JPG'),
(null, '흐릿함','1','방어도(5→8)', '특별', 'Silent', '스킬','방어도를 5 얻습니다. 내 다음 턴까지 방어도를 잃지 않습니다.','유사 바리게이트효과를 얻는다는 점에서 엄청 좋은카드. 특히 1코스트에 얻는부분에서 오히려 더 좋은 부분도 있다.', 'Blur.JPG'),
(null, '탄성 플라스크','2','반복횟수(3번→4번)', '특별', 'Silent', '스킬','무작위 적에게 중독 3을 부여합니다. 3번 반복합니다.','단일대상일 때 엄청난 중독을 부여하고, 스네코의 해골 유물과 특히 시너지가 엄청좋다.', 'Bouncing Flask.JPG'),
(null, '계산된 도박','0','소멸제거', '특별', 'Silent', '스킬','손에 있는 카드를 모두 버립니다. 버린 카드 개수만큼 카드를 뽑습니다.(소멸)','덱회전에 큰 도움을 주는 카드. 노코스트에 버리기시너지를 가볍게 내기에도 좋다.', 'Calculated Gamble.JPG'),
(null, '촉매','1','증가량(2배→3배)', '특별', 'Silent', '스킬','적 하나의 중독 수치를 2배로 증가시킵니다.(소멸)','독덱의 필살기카드. 엄청난 포텐셜을 가지고 있다.악몽-촉매로 몇배의 독을 넣을수도 있다.', 'Catalyst.JPG'),
(null, '집중','0','버리는 카드(3장→2장)', '특별', 'Silent', '스킬','카드를 3장 버립니다. 에너지 2개를 얻습니다.','코스트 펌핑카드.하지만 엄청난 수의 카드를 버려야 하기 때문에 상당한 부담이 되는 것이 단점', 'Concentrate.JPG'),
(null, '신경독','2','중독(4→7)', '특별', 'Silent', '스킬','적 전체에게 중독 4, 약화 2을 부여합니다.(소멸)','광역 독과 약화를 부여하여 공방 모두 도움이 된다.', 'Crippling Cloud.JPG'),
(null, '방해','1','비용(1→0)', '특별', 'Silent', '스킬','무작위 스킬 카드 1장을 손으로 가져옵니다. 가져온 카드는 이번 턴에만 비용이 0이 됩니다.(소멸)','운빨카드.', 'Distraction.JPG'),
(null, '탈출구','0','방어도(3→5)', '특별', 'Silent', '스킬','카드 1장을 뽑습니다. 뽑은 카드가 스킬 카드일 경우 방어도를 3 얻습니다.','드로우 손해없이 방어도도 확률적으로 얻을 수 있는 카드.', 'Escape Plan.JPG'),
(null, '다리 걸기','2','약화(2→3), 방어도(11→14)', '특별', 'Silent', '스킬','적 하나에게 약화 2를 부여합니다. 방어도를 11 얻습니다.','약화와 방어도가 동시에 있어 방어에 엄청 좋은 카드.', 'Leg Sweep.JPG'),
(null, '반사 신경','사용불가','뽑는 카드(2장→3장)', '특별', 'Silent', '스킬','이 카드를 버리면 카드를 1장 뽑습니다.','버리기덱의 핵심카드.', 'Reflex.JPG'),
(null, '설치','1','비용(1→0)', '특별', 'Silent', '스킬','손에 들고 있는 카드 1장을 덱의 맨 위에 놓습니다. 그 카드는 사용하기 전까지 비용이 0이 됩니다.','고비용의 스킬이나 파워를 0코스트로 사용할 수 있게 해주는 카드. 하지만 실제로는 사용하기가 애매한 카드. 강화가 필수적이라는 점도 애매하다.', 'Setup.JPG'),
(null, '공포','1','비용(1→0)', '특별', 'Silent', '스킬','적 하나에게 취약 99를 부여합니다.(소멸)','적 하나에게 죽기 전까지 사실상 영구지속 디버프라는 점에서 엄청 좋은 카드.', 'Terror.JPG'),
(null, '전문성','1','카드 개수(6장→7장)', '특별', 'Silent', '스킬','손에 들고 있는 카드가 6장이 될 때까지 카드를 뽑습니다.','조건에 따라 많은 카드를 드로우할 수 있다는 점에서 좋다. 하지만 제한된 코스트때문에 사용이 제한될 수 있다는 단점이 있다.', 'Expertise.JPG'),
(null, '전략가','비용없음','얻는 코스트(1개→2개)', '특별', 'Silent', '스킬','이 카드를 버리면 코스트 1개를 얻습니다.','사일런트 버리기 덱의 핵심.간단하게 코스트를 펌핑하게 해준다.', 'Tactician.JPG'),
(null, '시체 폭발','2','중독(6→9)', '희귀', 'Silent', '스킬','적 하나에게 중독 6을 부여하고 해당 적 사망시 그 최대 체력만큼 모든 적에게 피해를 줍니다.','사기 광역기 카드. 특히 체력이 높은데 여러마리 있는 몬스터의 경우 이 카드만 있으면 한 마리만 잡아도 해결이 되는 사기 카드이다.', 'Corpse Explosion.JPG'),
(null, '아드레날린','0','얻는 코스트(1개→2개)', '희귀', 'Silent', '스킬','에너지 1개를 얻습니다. 카드를 2장 뽑습니다.','노페널티로 코스트와 드로우를 모두 얻는 개사기카드', 'Adrenaline.JPG'),
(null, '연금','1','비용(1→0)', '희귀', 'Silent', '스킬','무작위 포션 1개를 얻습니다.(소멸)','강화시 노코스트가 되는 점에서 이득을 누적시키기 좋다. 무엇보다 불가침과 병 속의 유령을 얻을 확률이 증가한다는 점에서 충분히 가치가 있다.', 'Alchemize.JPG'),
(null, '불릿 타임','3','비용(3→2)', '희귀', 'Silent', '스킬','이번 턴에 더 이상 카드를 뽑을 수 없습니다.이번 턴에 손에 들고 있는 카드의 비용이 0이 됩니다.','코스트 사기를 칠 수 있게 해주는 유용한 카드. 그냥 쓰면 4장만 활용가능해서 애매한 경우도 있다. 드로우를 많이 해서 사용해야한다.', 'Bullet Time.JPG'),
(null, '폭주','1','다음스킬카드(1장→2장)', '희귀', 'Silent', '스킬','이번 턴에 사용하는 다음 스킬 카드를 2번 사용합니다.','범용성 높고 사용하기에따라 엄청나게 사기가 되는 카드. 고효율카드와 연계하면 엄청난 효과를 얻을 수 있다.', 'Burst.JPG'),
(null, '도플갱어','X','뽑는 카드(X장→X+1장), 코스트(X개→X+1개)', '희귀', 'Silent', '스킬','모든 에너지를 소모합니다. 다음 턴에 카드를 X장 더 뽑고 에너지를 X개 추가로 얻습니다.(소멸)','희귀카드이지만 쓰레기카드. 사용하면 손해를 보는 신기한 카드', 'Doppelganger.JPG'),
(null, '악몽','3','비용(3→2)', '희귀', 'Silent', '스킬','손에 들고 있는 카드 하나를 선택합니다. 다음 턴에 해당 카드를 3장 복사하여 손에 가져옵니다.(소멸)','사일런트 최고의 폭발력을 자랑한다. 무거운 코스트를 가지고 있지만 그만큼 리턴이 확실하다.', 'Nightmare.JPG'),
(null, '환영 살인마','1','비용(1→0)', '희귀', 'Silent', '스킬','다음 턴에 적에게 입히는 피해량이 2배로 증가합니다.','패치전에는 애매했으나 패치후에 1코스트가 된 후로 가볍게 쓰기에도 좋다.', 'Phantasmal Killer.JPG'),
(null, '강철의 폭풍','2','(단도→강화된 단도)', '희귀', 'Silent', '스킬','손에 있는 카드를 모두 버립니다. 버린 카드 개수만큼 단도 카드를 얻습니다.','단독으로 쓰기에는 어렵고 시너지를 맞추기에도 어렵다.', 'Storm of Steel.JPG'),
(null, '불쾌','X','힘 감소량(X→X+1), 약화(X→X+1)', '희귀', 'Silent', '스킬','모든 에너지를 소모합니다. 적 하나의 힘을 X만큼 감소시키고 약화 X를 부여합니다.(소멸)','훌륭한 성능의 방어카드. 힘감소는 전투내내 지속되기 때문에 꽤 강력한 디버프이다.', 'Malaise.JPG'),
(null, '마름쇠','1','피해(3→5)', '특별', 'Silent', '파워','공격을 받을 때마다 공격자에게 3의 피해를 입힙니다.','방어력은 좋은데 공격수단이 부족할 때 사용하기에 좋다. 특히 심장에서 사용하면 매우 좋다.', 'Caltrops.JPG'),
(null, '발놀림','1','민첩(2→3)', '특별', 'Silent', '파워','민첩 2를 얻습니다.','방어카드의 효율을 크게 올려주는 좋은카드.', 'Footwork.JPG'),
(null, '무한의 검날','1','선천성 부여', '특별', 'Silent', '파워','매 턴마다 단도 카드 한 장을 얻습니다.','단도를 매 턴마다 얻을 수 있는 파워카드. 검무가 상향되서 애매한 카드가 되었다.같은 코스트로 검무한번으로 단도를 얻을 때 이 카드는 4턴을 기다려야된다.', 'Infinite Blades.JPG'),
(null, '유독가스','1','중독(2→3)', '특별', 'Silent', '파워','매 턴마다 모든 적에게 중독 2를 부여합니다.','독덱을 위한 카드. 인공물빼기에도 좋다. 강화의존도가 높은편이다.', 'Noxious Fumes.JPG'),
(null, '괜찮은 전략','0','선택하는 카드(최대1장→최대2장)', '특별', 'Silent', '파워','매 턴마다 카드를 1장 보존합니다.','매 턴 패가 늘어나는 효과를 얻을 수 있고 전투설계에도 매우좋다.', 'Well_Laid Plans.JPG'),
(null, '정밀','1','증가하는 피해량(4→6)', '특별', 'Silent', '파워','단도 카드의 피해량이 4만큼 증가합니다.','단도덱의 화력을 담당하는 카드. 강화 증가치가 높아 강화의존도가 크다.', 'Accuracy.JPG'),
(null, '능지처참','2','피해(1→2)', '희귀', 'Silent', '파워','카드를 사용할 때마다 모든 적에게 1의 피해를 입힙니다.','사일런트 특성상 카드를 많이 사용하는 만큼 준수한 성능을 가지고 있다. 특히 광역딜이라 더욱 좋다.', 'A Thousand Cuts.JPG'),
(null, '잔상','1','선천성부여', '희귀', 'Silent', '파워','카드를 사용할 때마다 방어도를 1 얻습니다.','사일런트에게 안정감을 심어주는 훌륭한 카드. 특히 카드를 많이쓰는 덱에 좋다. 독덱 또한 큰 효율은 아니지만 그럼에도 불구하고 많은 방어도를 얻을 수 있어 좋다.', 'After Image.JPG'),
(null, '독 바르기','2','비용(2→1)', '희귀', 'Silent', '파워','적이 공격으로 인해 체력을 잃을 때마다 중독 1을 부여합니다.','단도덱에서 고려해볼만한 카드. 기본코스트가 높아서 강화하지 않으면 사용하기 쉽지않다. 기본성능도 미묘한편', 'Envenom.JPG'),
(null, '작업도구','1','비용(1→0)', '희귀', 'Silent', '파워','매 턴마다 카드를 1장 뽑고 카드를 1장 버립니다.','단순하게 보면 0드로우지만 자세히보면 버리기 카드 시너지를 내줄 수 있다. 특히 팅샤나 붕대와 같은 유물의 효과를 매 턴 볼 수 있게 해준다.', 'Tools of the Trade.JPG'),
(null, '유령의 형상','3','불가침(2→3)', '희귀', 'Silent', '파워','불가침을 2 얻습니다. 턴 종료시 민첩을 1 잃습니다.','최강의 시간벌이용 카드. 강화의존도가 높은편이다. 인공물과 연계해 아예 디버프를 없애는 방법도 있다. 못 없앴다면 불가침끊기기 전에 죽이는 방법을 써야된다.', 'Wraith Form.JPG'),
(null,'타격','1','피해(6→9)','일반','Defect','공격','6의 피해를 줍니다.','디펙트의 기본공격카드. 이 카드 역시 덱 압축할 때 최우선순위 중 하나이다.','Strike_Defect.JPG'),
(null,'최적화','2','피해(15→20)','일반','Defect','공격','피해를 15 줍니다. 최적화 카드를 사용할 때마다 이번 전투 동안 이 카드의 비용이 1 감소합니다.','초반용 딜카드. 깡딜이 제법 강하고 사용할수록 비용이 줄어드는 부가효과도 있지만 처음쓸 때의 코스트가 너무 비싸다. 한번에 깡딜이 필요한 1막엘리트에서 자주사용한다.','Streamline.JPG'),
(null,'광선 휩쓸기','1','피해(6→9)','일반','Defect','공격','적 전체에게 피해를 6 줍니다. 카드를 1장 뽑습니다.','광역딜카드. 초반잡몹을 잡기위해 선택하기도 한다. 피해량이 낮아서 강화를 해야한다. 하지만 다른 필수강화카드가 많아서 집기가 애매한 카드.','Sweeping Beam.JPG'),
(null,'꽃샘추위','1','피해(6→9)','일반','Defect','공격','피해를 6 줍니다. 냉기를 1번 영창합니다.','공방을 겸하는 카드. 밀집만 갖춘다면 후반에도 쓸만한 카드이다.','Cold Snap.JPG'),
(null,'눈 할퀴기','0','피해(3→4), 약화(1→2)','일반','Defect','공격','피해를 3 줍니다. 적이 공격할 의도가 있다면 약화를 1 부여합니다.','무력화의 하위호환카드. 그래도 유일하게 약화디버프를 걸 수 있는 카드이다.','Go for the Eyes.JPG'),
(null,'되돌리기','1','피해(9→12)','일반','Defect','공격','피해를 9 줍니다. 이번 턴에 사용하는 다음 카드를 뽑을 카드 더미의 맨 위에 놓습니다.','키카드를 자주 쓰게 만들어주는 카드. 다음턴설계에도 좋다.','Rebound.JPG'),
(null,'레이저 포인터','0','피해(3→4), 취약(1→2)','일반','Defect','공격','피해를 3 주고 취약을 1 부여합니다.','구체덱에서는 애매한 카드지만 인공물을 없애고 0코덱에서는 자주 쓰이는 카드.','Beam Cell.JPG'),
(null,'번개 구체','1','피해(7→10)','일반','Defect','공격','피해를 7 줍니다. 전기를 1번 영창합니다.','즉발딜과 영창을 겸하는 카드. 구체딜까지 생각하면 매우 준수한 딜링카드.','Ball Lightning.JPG'),
(null,'일제 사격','1','피해(4→6)','일반','Defect','공격','영창된 구체 하나당 피해를 4 줍니다.','구체의 숫자를 늘렸다면 강력한 공격 카드. 특히나 고승천에서는 방어를 위해 구체 슬롯을 냉기로 가득 채워야 하는 경우가 많아서 딜이 부족해지는데, 사용 횟수에 비례해 딜이 증가하는 눈보라나 후벼 파기에 비해 피해량을 증가시키는데 필요한 난이도와 시간이 적다. ','Barrage.JPG'),
(null,'후벼 파기','0','기본 피해량(3→5)','일반','Defect','공격','피해를 3 줍니다. 이번 전투에서 모든 후벼 파기 카드의 피해량이 2 증가합니다.','하위모 덱 주력 딜링 카드. 광란과 달리 후벼 파기는 하나만 쓰면 나머지 후벼 파기도 강화되기에 적당히 여러 장을 집어주는 게 포인트.','Claw.JPG'),
(null,'컴파일 드라이버','1','피해(7→10)','일반','Defect','공격','피해를 7 줍니다. 영창된 구체의 종류 하나당 카드를 1장 뽑습니다.','폼멜 타격의 디펙트 버전. 시작 유물 덕분에 구체가 1종류 이상 있을 확률은 크지만 3종류 이상 떠있는 경우는 드문지라 평균적으로 1~2장을 드로우해주는 카드로 봐도 무방하다. ','Compile Driver.JPG'),
(null,'FTL','0','피해(5→6), 사용 매수 제한(3→4)','특별','Defect','공격','피해를 5 줍니다. 만약 이번 턴에 카드를 3장 미만으로 사용했다면 카드를 1장 뽑습니다.','조건부로 패 손해 없이 공짜 딜을 넣을 수 있는 공격 카드. 다만 턴 초반에 써야 한다는 제한 사항으로 인해, 드로우나 하위모로 턴 중간에 뽑는 경우 추가 드로우를 할 수 없는 경우도 많고, 카드 자체의 성장성은 떨어지므로 적당량 집는 것이 좋다.','FTL.JPG'),
(null,'긁어내기','1','피해(7→10), 카드 뽑기(4→5)','특별','Defect','공격','피해를 7 줍니다. 카드를 4장 뽑습니다. 뽑은 카드 중 비용이 0이 아닌 카드는 버립니다.','덱에 후벼 파기 등 0코스트 카드가 많다면 고려해 볼 만하다. 다만 긁어내기 자체는 0코스트 카드가 아니라서 덱에 2장 이상 넣는 것은 추천하지 않는다. 게다가 긁어내기를 하위모 덱에 쓰기에는, 핵심 카드인 하나를 위한 모두를 버릴 수 있어 좀 애매하다. ','Scrape.JPG'),
(null,'날뛰기','1','피해(7→9)','특별','Defect','공격','무작위 적에게 피해를 7만큼 2번 줍니다.','단순 계산 시 무려 1코 14(18)딜의 무식한 깡딜 카드. 레이저 포인터, 파괴광선과 함께 깡딜 덱의 주요 카드다.','Rip and Tear.JPG'),
(null,'눈보라','1','배수(2→3)','특별','Defect','공격','이번 전투에서 영창된 냉기의 2배만큼 적 전체에게 피해를 줍니다.','냉기 덱의 핵심 공격 카드.1코스트 광역딜. 냉기덱이 아니라면 충분한 딜이 나오기까지 오랜시간이 걸려 차용하기 애매하다.','Blizzard.JPG'),
(null,'암울한 전망','2','피해(10→14)','특별','Defect','공격','적 전체에게 피해를 10 줍니다. 암흑을 1번 영창합니다.','광역기로 채택하기에는 코스트에 비해 피해량이 낮고, 암흑 구체용으로 넣기에도 어둠이라는 상위 호환급 카드가 존재하기에 조금 모호한 카드이다.','Doom and Gloom.JPG'),
(null,'용해 장치','1','피해(10→14)','특별','Defect','공격','적의 모든 방어도를 없앱니다. 피해를 10 줍니다.','구체 수호기 등 방어도를 쌓는 적들을 상대로 굉장한 위력을 자랑한다. 특히 2막의 보스 셋과 시간 포식자, 도누와 데카를 비롯해 방어도를 쌓는 적들은 상당히 많기 때문에 쏠쏠히 활약하며, 4막에서 방패의 무식한 방어도 패턴을 씹을 수 있게 해준다. 방어도 5~6 정도만 있어도 딜 10(14)와 합쳐져 1코스트 15+a 의 즉발 딜이어서 운이 좋으면 제대로 사기칠 수 있는 카드.','Melter.JPG'),
(null,'찢기','3','피해(24→32)','특별','Defect','공격','피해를 24 줍니다. 만약 적이 죽으면 에너지를 3 얻습니다.','깡딜 카드 중 하나로 조건부로 노코스트에 강력한 피해를 줄 수 있다. 특히 다수 적을 상대로 하나를 빠르게 잘라내기가 좋기에 광역기가 부족한 디펙트에게 유사 광역기처럼 활용될 수도 있다. ','Sunder.JPG'),
(null,'명중','1','피해(8→11), 조준(2→3)','특별','Defect','공격','피해를 8 줍니다. 대상에게 조준 2을 부여합니다.','구체 덱의 딜링 카드. 대상에게 구체 한정 취약을 걸어주므로 딜 템포를 올리는 데 좋다. 밀집이 충분히 있다면 강력한 피해를 줄 수 있으며 초반에 빠르게 첨탑을 등반하는 데에도 쓸만하다.','Bullseye.JPG'),
(null,'유성 타격','5','피해(24→30)','희귀','Defect','공격','피해를 24 줍니다. 플라즈마를 3번 영창합니다.','플라즈마 덱의 키 카드. 전용 세팅 없이는 쓰기 힘든 카드로, 코스트가 5나 되기에 플라즈마 덱이 아니거나 5코스트 덱이 아니라면 저주 카드나 다름없다. 다만 디펙트는 합하기, 에너자이저, 배터리 충전, 터보 등 에너지 펌핑이 상대적으로 쉬운 편이라 조건을 맞추기가 막 어렵지는 않다.','Meteor Strike.JPG'),
(null,'전류 타격','3','피해(7→9)','희귀','Defect','공격','이번 전투에서 영창된 전기 하나당 무작위 적에게 피해를 7 줍니다.','전기 덱의 필살기. 강화하지 않아도 전기를 5번 영창하는 것만으로 몽둥이질보다 높은 피해가 나온다. 다만 무작위 대상이라 적이 많을 경우 효율이 떨어지는 편. 연타 공격이기 때문에 힘 배율이 적용되고 레이저 포인터의 취약을 받고 적의 가시에 반격당하며, 밀집 보너스 적용도 받지 못한다.','Thunder Strike.JPG'),
(null,'파괴광선','2','피해(26→34)','희귀','Defect','공격','적 전체에게 피해를 26 줍니다. 밀집을 3 잃습니다.','2막 보스 청동 자동 인형이 쓰는 바로 그 기술. 다만 이름만 같고 효과는 다소 다르다. 플라즈마 덱의 주력 딜링 카드. 밀집을 3이나 잃기 때문에 일반 구체의 효율이 급격하게 하락하지만 플라즈마 구체는 밀집 스택이 아무 의미가 없기 때문에 상관이 없고, 압도적인 광역 공격이 모든 걸 상쇄해준다.','Hyperbeam.JPG'),
(null,'하나를 위한 모두','2','피해(10→14)','희귀','Defect','공격','피해를 10 줍니다. 버린 카드 더미에 있는 비용이 0인 카드를 모두 손으로 가져옵니다.','통칭 하위모 혹은 올포원.피해량은 평범하지만 부가 효과가 상당히 좋다. 후벼 파기와 같은 고효율 0코 카드를 여러 장 집은 덱이라면 무조건 챙겨가는 카드. ','All for One.JPG'),
(null,'코어 서지','1','(→)','희귀','Defect','공격','피해를 11 줍니다. 인공물을 1 얻습니다.(소멸)','1코스트에 준수한 피해량, 특히 여러모로 유용한 인공물 덕에 일단 집어두면 도움이 된다. 주력 콤보는 일명 서지편향이라고 불리는 인지 편향과의 연계로, 디버프 없이 순수하게 밀집 4(5)를 얻을 수 있어 강화해도 밀집 2를 주는 조각 모음과 비교해볼 때 매우 강력한 성능을 보여준다.','Core Surge.JPG'),
(null,'수비','1','방어(5→8)','일반','Defect','스킬','방어도를 5 얻습니다.','디펙트의 기본 카드. 타격보다는 제거 우선 순위는 밀리는 편이나 덱의 공격 카드의 비율을 보고 먼저 제거할 수도 있다. 타수가 적은 디펙트라 타 캐릭터보다 상대적으로 지우기 쉽다.','Defend_Defect.JPG'),
(null,'이중 시전','1','비용(1→0)','일반','Defect','스킬','가장 오른쪽의 구체를 2번 발현합니다.','디펙트의 기본 카드. 초반 영창된 전기를 빠르게 강력한 딜로 전환해주며, 방어력이 심심한 냉기 역시 방어도로 전환해준다. 특히 상급 구체인 플라즈마나 어둠 구체를 이중 시전하는 경우 에너지 펌핑이나 극딜에 엄청난 차이가 발생하므로, 가장 오른쪽 구체를 무엇으로 두고 이중 시전으로 언제 발현하는지를 조정하는 것이 디펙트의 전략이 된다.','Dualcast.JPG'),
(null,'파지직','1','비용(1→0)','일반','Defect','스킬','전기를 1번 영창합니다.','디펙트의 기본 카드. 강화하지 않으면 효율이 매우 낮은데 강화해도 썩 좋지는 않아 구체 계의 타격으로 불린다. 다만 강화하면 0코 구체 시전이라는 특이점 때문에 쓸 수는 있는 성능이 된다. 따라서 쓰겠다면 강화해야 한다.','Zap.JPG'),
(null,'냉정함','1','카드 뽑기(1→2)','일반','Defect','스킬','냉기를 1번 영창합니다. 카드를 1장 뽑습니다.','냉기 영창과 함께 드로우까지 하는 카드. 비슷한 포지션의 흘려보내기와는 달리 강화 시 방어력이 아닌 드로우 매수가 늘어나므로 추가 드로우 카드로서의 역할도 겸할 수 있다. 반면 밀집이 없는 경우 당장 턴에 제공해주는 방어도 자체는 그리 높지 않으므로, 에너지가 부족한 초반에 방어도를 얻기 위한 수단으로는 다소 애매할 수는 있다.','Coolheaded.JPG'),
(null,'도약','1','방어도(9→12)','일반','Defect','스킬','방어도를 9 얻습니다.','수비 카드의 상위 호환. 얻는 방어도 수치는 꽤 높지만 너무 단순한 성능이라 아래의 배터리 충전에 비하면 인기는 조금 밀리는 편. 아이언클래드의 흘려보내기는 거의 비슷한 방어도를 제공하면서도 드로우가 있기 때문에 더욱 애매한 성능이 부각된다.','Leap.JPG'),
(null,'배터리 충전','1','방어도(7→10)','일반','Defect','스킬','방어도를 7 얻습니다. 다음 턴에 에너지를 1 얻습니다.','방어도도 챙기는 동시에 다음 턴에 에너지까지 하나 추가로 주는 준수한 카드. 적의 공격이 없더라도 쓰면 다음 턴에 이득을 볼 수 있다. 다만 그 자체로는 제공하는 방어도가 낮기에 조금 난감한 상황이 발생할 수는 있다.','Charge Battery.JPG'),
(null,'스택','1','방어도 3추가','일반','Defect','스킬','버린 카드 더미에 있는 카드의 수만큼 방어도를 얻습니다.','사서 디펙트의 키 카드. 후반에 40장 넘게 카드를 모으기도 하는 사서 디펙트의 경우 1코스트만으로 수십 방어도를 쌓는 엄청난 성능을 낼 수 있다. 사서 디펙트가 아니더라도 덱의 크기가 적당히 크다면 상황에 따라 강화된 스택 카드 한 장만으로도 엄청난 방어도를 쌓을 수 있어 한 장 정도는 넣을 만하다.','Stack.JPG'),
(null,'재귀','1','비용(1→0)','일반','Defect','스킬','가장 오른쪽의 구체를 발현합니다. 발현된 구체를 영창합니다.','전기나 냉기 위주의 덱에서는 강화되지 않은 상태라면 1 코스트로 발현 밸류 하나를 얻는 것이므로 효율이 그다지 좋지 않다. 제대로 쓰기 위해서는 강화가 필수. 발현된 구체가 암흑이었을 경우 스택을 유지한 채 영창하므로 암흑 덱에 채용된다. 플라즈마 구체 역시 밀려서 사라지지 않게 해주기 때문에, 플라즈마 덱 혹은 플라즈마 전지 유물 등으로 플라즈마 구체를 소소하게 활용하는 경우 강화된 상태로 얻을 수 있다면 한 장 정도 넣으면 도움이 된다.','Recursion.JPG'),
(null,'증기 장벽','0','방어도(6→8)','일반','Defect','스킬','방어도를 6 얻습니다. 이번 전투 동안 이 카드의 방어도가 1 감소합니다.','짧은 전투에서는 좋은 에너지 효율을 보이나 장기전에서는 성능이 애매해진다. 그래도 0 코스트여서 부담 없이 쓸 수 있고, 사용 후에 강화된 홀로그램으로 가져와 다시 쓰는 용도 등으로도 활용할 수 있다.','Steam Barrier.JPG'),
(null,'터보','0','에너지(2→3)','일반','Defect','스킬','에너지를 2 얻습니다. 버린 카드 더미에 공허 카드를 섞어 넣습니다.','1 드로우와 1 에너지를 미래에 손해보는 대신, 지금 즉시 2(3) 에너지를 얻는 카드이다. 디펙트가 가볍고 빠르게 에너지를 얻는 수단이 된다. 디펙트에겐 고코스트의 파워 카드들이 많기에 덱에 그런 카드가 많다면 한 장쯤 집어두는 것이 현명하다. 특히 사서 덱은 특성상 상태이상에 강하기 때문에 보다 편하게 사용할 수 있다.','TURBO.JPG'),
(null,'홀로그램','1','방어도(3→5)','일반','Defect','스킬','방어도를 3 얻고 버린 카드 더미에 있는 카드 1장을 선택하여 손으로 가져옵니다.(소멸)','굉장히 훌륭한 서치 카드.키 카드를 한 번 더 사용할 수 있게 하거나, 0코스트 카드를 쓴 후에 다시 쓰거나, 버린 카드 더미에 있는 터보나 오버클럭 등의 카드를 다시 사용해 에너지나 드로우를 뻥튀기하거나, 긁어내기로 버려진 카드를 가져오거나, 급한 상황에서 방어 카드나 버퍼를 불러오는 등 활용도는 무궁무진하다.','Hologram.JPG'),
(null,'강화된 몸체','X','방어도(7→9)','특별','Defect','스킬','방어도 7을 X번 얻습니다.','플라즈마 덱의 핵심 방어 카드. 디펙트에겐 에너지 펌핑 수단이 워낙 많기 때문에 제대로 쓰면 엄청난 방어력을 제공해준다. 특히 플라즈마 덱에 에너자이저나 초반 합하기카드와 연동시킨다면 수십~수백 방어도까지도 쌓을 수 있게 해준다.','Reinforced Body.JPG'),
(null,'걷어내기','1','카드 뽑기(3→4)','특별','Defect','스킬','카드를 3장 뽑습니다.','디펙트의 대표적인 드로우 카드. 심플하게 드로우 3(4)장을 제공한다. 매수만 놓고보면 사일런트의 곡예보다 효율이 높지만 일반 카드라 심심하면 보이는 곡예와는 달리 걷어내기는 특별 카드라 얻기가 쉽지 않다. ','Skim.JPG'),
(null,'뇌우','X','영창 수(X→X+1)','특별','Defect','스킬','전기를 X번 영창합니다.(소멸)','에너지를 전부 전기 구체로 전환하므로 에너자이저 등 각종 에너지 펌핑 수단과 연계가 좋다. 혹은 플라즈마와 연계된 전기 덱의 경우에도 빠르게 전기 구체를 영창할 수 있게 해준다. 에너지와 밀집을 충분히 확보할 수 있으면 자체 화력도 출중한 편. 모든 에너지를 사용하기에 방어가 취약해질 수 있으므로 방어 수단을 잘 마련해야 한다. 전류타격과의 시너지가 좋다.','Tempest.JPG'),
(null,'백색 소음','1','비용(1→0)','특별','Defect','스킬','무작위 파워 카드 1장을 얻습니다. 이번 턴 동안 그 카드의 비용이 0이 됩니다.(소멸)','무작위 파워기는 하지만, 디펙트는 고코스트 고성능 파워 카드가 많고 반복, 조각모음, 축전기 등 1코스트 파워도 준수한 경우가 많아 성능은 괜찮은 편이다.','White Noise.JPG'),
(null,'부팅 과정','0','방어도(10→13)','특별','Defect','스킬','방어도를 10 얻습니다. (선천성)(소멸)','예열이 필요한 디펙트의 안정성을 크게 높여주어 매우 유용하다. 강화된 오한과 취사선택하게 되며 이 둘 중 하나는 가능하면 반드시 얻어 두는 것이 좋다. 다만 선천성이 붙은 관계로 2개 이상을 집으면 첫 패가 답답해지는 경우가 많으니 주의.','Boot Sequence.JPG'),
(null,'빙하','2','방어도(7→10)','특별','Defect','스킬','방어도를 7 얻습니다. 냉기를 2번 영창합니다.','방어도를 얻으면서 더불어 냉기를 2번 영창하기 때문에 뛰어난 안정성을 제공해준다. ','Glacier.JPG'),
(null,'소비','2','밀집(2→3)','특별','Defect','스킬','밀집을 2 얻습니다. 구체 슬롯을 1개 잃습니다.','구체 덱에서 인지 편향, 조각모음과 함께 쓰이는 밀집 카드. 조각모음보다 빠르게 밀집을 확보해주나, 사용하기에는 여러모로 까다롭다. 먼저 2코스트여서 다소 부담된다는 점. 또한 파워 카드나 소멸 카드가 아니라서 덱이 순환되면 패로 다시 돌아오므로 축전기나 투입기가 없는 경우 패에서 계속 놀게 되기가 쉽기도 하다.그러나 디펙트가 밀집을 얻는 수단은 제한적일 뿐더러 제공하는 밀집 수치가 높기에 상황을 보며 선택하게 된다.','Consume.JPG'),
(null,'어둠','1','모든 암흑 구체의 지속효과를 발동시킨다.','특별','Defect','스킬','암흑을 1번 영창합니다.','암흑 덱의 키카드. 능동적으로 암흑 구체의 스택을 쌓을 수 있어 딜을 빠르게 올릴 수 있다. 눈보라가 없거나 딜 카드가 부족한 냉기 덱에 채용하여 부족한 딜을 메꾸기도 한다. 강화 효율도 좋다.','Darkness.JPG'),
(null,'오버클럭','0','카드 뽑기(2→3)','특별','Defect','스킬','카드를 2장 뽑습니다. 화상 카드를 버린 카드 더미에 넣습니다.','0코스트로 2(3)드로우를 제공한다. 걷어내기와 달리 노코스트이므로 가볍게 쓸 수 있다. 다만 디펙트는 자체적으로 화상카드를 제거할 수단이 재활용밖에 없으며 재활용으로 코스트가 없는 화상을 소멸시켜도 별 이득이 없다. 즉 구급상자라도 있지 않다면 시간이 지날수록 덱에 화상이 차게 되어 드로우가 취약해진다. ','Overclock.JPG'),
(null,'역장','4','방어도(12→16)','특별','Defect','스킬','방어도를 12 얻습니다. 이번 전투 중 사용한 파워 카드의 수만큼 비용이 감소합니다.','파워 카드가 적은 초반에는 거의 쓸모가 없다. 후반에 파워 카드를 많이 뽑을 경우 좋은 효율을 보이나, 정작 파워 카드를 많이 전개하는 덱의 경우에도 예열 과정에서 편하게 사용하기가 어렵기 때문에 다소 애매하다. 따라서 창공지능 덱보다는 파워를 적당 수 사용하여 0~1코스트로 활용할 수 있는 일반 덱에서 쓰는 것이 좋다. ','Force Field.JPG'),
(null,'에너자이저','1','비용(1→0)','특별','Defect','스킬','에너지를 두 배로 만듭니다.(소멸)','다른 캐릭터들에게도 한두 개씩 있는 에너지 카드. 이 카드의 경우 드로우가 없는 대신 심플하게 에너지를 두 배로 불려준다. 합하기나 터보 등과 연계하여 에너지를 제대로 뻥튀기할 수도 있고, 플라즈마 덱이 사용할 경우 에너지가 20을 넘어가는 희한한 풍경도 나온다. 다중시전, 뇌우, 강화된 몸체 등과 같은 X코스트 카드들과 시너지가 좋다.단 이 카드를 사용한 뒤의 에너지를 기준으로 곱연산하기 때문에 강화를 안 했을 때의 성능은 상당히 제한적이라는 점에 주의할 것.','Double Energy.JPG'),
(null,'오한','0','선천성부여','특별','Defect','스킬','전투 중인 적 하나당 냉기를 1번 영창합니다.(소멸)','부팅 과정과 함께 전투 초반에 안정성을 높여주는 카드.재구성 덱이나 플라즈마 덱을 제외하면 누구라도 골라봄 직하며, 플라즈마 덱이라도 초반 안정성을 위해 고려해 볼 만하다.','Chill.JPG'),
(null,'유전 알고리즘','1','증가량(2→3)','특별','Defect','스킬','방어도를 1 얻습니다. 카드를 사용하면 이 카드의 방어도를 영구적으로 2 증가시킵니다.(소멸)','초반에 나오면 중후반부에 큰 도움이 되기 때문에 1막 후반 ~ 2막 초반까지는 집어 두면 좋다. 무한 모드는 물론이고 일반 게임에서도 후반부에 가면 무적 카드의 상위호환 수준의 위력을 발휘한다.','Genetic Algorithm.JPG'),
(null,'융합','2','비용(2→1)','특별','Defect','스킬','플라즈마를 1번 영창합니다.','플라즈마 덱의 키 카드. 딱히 구체 슬롯을 늘리지 않고 구체를 넣어놓아도 꽤 풍족한 에너지를 얻을 수 있다. 플라즈마로 얻은 에너지를 모두 사용하기 위해 다시 시작이나 걷어내기 등 각종 드로우 수단을 보조해주고, 파괴광선과 찢기, 유성 타격 등 고코스트 딜링 수단을 챙기는 것이 좋다. 다만 코스트가 부담스러워 강화 의존도가 크다는 것이 단점. 또한 예열이 오래 걸리기에 반드시 취약한 순간이 생긴다는 점에 주의해야 한다.','Fusion.JPG'),
(null,'자동 방패','1','방어도(11→15)','특별','Defect','스킬','방어도가 0이면, 11 방어도를 얻습니다.','조건만 맞춘다면 1 코스트에 11(15)의 효율 좋은 방어도를 주는 카드. 방어 수단이 부족한 초반에는 빠르게 코스트를 방어도로 환산해준다.','Auto-Shields.JPG'),
(null,'재구성','1','잃는 밀집(1→2), 힘(1→2), 민첩(1→2)','특별','Defect','스킬','밀집을 1 잃습니다. 힘을 1 얻습니다. 민첩을 1 얻습니다.','재구성 덱의 핵심 카드. 두 번이나 패치를 거친 카드로서, 처음에는 0코스트 예지 4를 주는 카드였으며 이후 밀집을 깎고 힘과 민첩을 올려주는 카드로 리메이크되었다. 이 때는 강화 여부와 상관 없이 힘과 민첩을 1씩밖에 올려주지 않아서 아무도 사용하지 않는 쓰레기 카드로 취급되었다. 결국 상향되어 강화 시에 1코스트로 힘과 민첩을 2씩 얻는 물리형 디펙트 카드로 재탄생했다.','Reprogram.JPG'),
(null,'재활용','1','비용(1→0)','특별','Defect','스킬','카드를 한장 소멸시킵니다. 그 카드의 비용만큼 에너지를 얻습니다.','소멸과 에너지 펌핑을 겸하는 카드. 후반에 덱에 필요 없는 고비용 카드가 많거나, Hello World 혹은 창의적인 인공지능 카드로 전투 중에 카드가 점점 늘어나는 덱이라면 덱 압축과 에너지 펌핑을 동시에 할 수 있게 해준다. 디펙트의 카드군에서 다른 카드를 소멸시키는 카드는 재활용밖에 없기 때문에 압축 순환 덱에도 채용되는 경우가 많다.','Recycle.JPG'),
(null,'평형','2','방어도(13→16)','특별','Defect','스킬','방어도를 13 얻습니다. 이번 턴 종료시 카드를 버리지 않습니다.','드로우와 방어도 양 쪽의 안정성을 높여주는 카드. 암흑 덱은 필수적으로 채용하는 것이 좋고,[28] 다른 덱에서도 패의 타이밍을 조절하는 데에 유용하다.','Equilibrium.JPG'),
(null,'합하기','1','요구 카드 수(4장→3장)','특별','Defect','스킬','뽑을 카드 더미에 있는 카드 4장당 에너지를 1 얻습니다.','원래는 6장(5장)당 1코스트를 올려주었기에 초반에 집기가 꺼려졌지만 요구하는 카드 수가 2장씩이나 줄어들면서 언제 집든 평균 1코스트 이상의 에너지 이득을 볼 수 있는 카드가 되었다. 특히 사서 덱의 경우에는 상향 전부터 키카드로서 선호도가 높았는데, 상향 이후에는 상황에 따라 초반에 뽑을 경우 10 에너지에 가까운 이득을 볼 수 있는 효율 좋은 카드가 되었다.','Aggregate.JPG'),
(null,'혼돈','1','영창(1→2)','특별','Defect','스킬','무작위 구체를 1번 영창합니다.','강화가 되지 않은 경우 랜덤성으로 인해 효율은 그다지 좋지 않아 강화 의존도는 상당히 높다. 강화 시 1코스트 2구체라는 높은 효율을 보여주므로 구체 덱 컨셉에서 초반에 빠르게 슬롯을 채우기 위해 채용한다. 시동이 걸린 후에는 구체 자체보다는 주로 2연속 발현 효과를 얻기 위해 사용한다. ','Chaos.JPG'),
(null,'다시 시작','0','카드 뽑기(4→6)','희귀','Defect','스킬','모든 카드를 뽑을 카드 더미에 섞어 넣은 뒤 카드를 4장 뽑습니다.(소멸)','덱 손패 꼬임을 방지해주는 카드. 해시계 유물 등과 시너지가 좋다. 패가 꼬일 위험이 높은 덱일 때 특히 유용하다. 혹은 터보나 합하기로 에너지를 펌핑시킨 후 손패를 보충해주는 역할로도 쓸 수 있다. ','Reboot.JPG'),
(null,'다중 시전','X','발현 수(X→X+1)','희귀','Defect','스킬','가장 오른쪽의 구체를 X번 발현합니다.','암흑 덱의 주력 카드.아이스크림이 있다면 에너자이저로 에너지를 모아서 한 턴에 수백 딜을 우겨넣을 수도 있다. 플라즈마 덱에서도 좋은 편으로, 에너지를 사용 당시의 에너지의 두 배+2까지 늘려준다.','Multi-Cast.JPG'),
(null,'무지개','2','소멸하지 않음','희귀','Defect','스킬','전기를 1번, 냉기를 1번, 암흑을 1번 영창합니다.(소멸)','에너지 2개를 사용하여 3개의 구체를 만들 수 있다는 점에서 초반에 채용할 수 있다. 강화되지 않은 카드 한 장만으로 다수의 구체를 빠르게 생성해 슬롯을 채워주거나 밀어내 발현시켜주는 점이 장점인 초반용 구체 영창 카드. 컴파일 드라이버와 시너지가 좋고, 상급 구체인 암흑을 영창해주는 용도로도 쓸 수 있다.다만 강화한 파지직, 어둠, 혼돈 등과 비교하면 효율은 다소 떨어진다.','Rainbow.JPG'),
(null,'증폭','1','파워 카드 장수(1→2)','희귀','Defect','스킬','이번 턴에 사용하는 다음 파워 카드가 두 번 사용됩니다.','사용하면 해당 전투 동안 다시 사용할 수 없는 파워 카드의 효율을 2배로 뽑아낼 수 있으며 창공지능을 택한 덱 등 파워 카드를 많이 사용하는 덱에서 빛을 발한다. 다만 파워 카드가 같이 손에 잡히지 않으면 점액질이나 다름없고, 증폭까지 쓰면서 파워 카드를 사용할 코스트가 부족한 경우도 많고, 파워 카드는 한 번 쓰면 사라지기 때문에 덱에 어지간히 파워 카드가 많거나 창의적인 인공지능을 쓰는 것이 아니라면 손에서 노는 경우가 많으니 덱의 상황을 보고 신중하게 집을 필요가 있다.','Amplify.JPG'),
(null,'탐색','0','카드 뽑기(1→2)','희귀','Defect','스킬','뽑을 카드 더미에서 카드 1장을 골라 손으로 가져옵니다.(소멸)','비용 0으로 원하는 카드를 뽑을 카드 더미에서 1~2장 서치해 올 수 있는 사기 카드. 전투 초반에 뽑으면 빠른 예열에 효과적이며 병 속의 번개와의 조합도 발군이다. 핵심 파워 카드나 신성 등 키 카드를 가져오는 용도로도 좋으며 강화하면 드로우 수단으로서도 가치가 높다.','Seek.JPG'),
(null,'핵분열','0','(구체 제거→구체 발현)','희귀','Defect','스킬','모든 구체를 소멸시키고, 소멸된 구체 하나당 에너지 1을 얻고 카드 1장을 드로한다.(소멸)','기본 카드는 구체를 소멸시키므로 강화 의존도가 심하게 높다. 강화 시 구체 발현으로 인한 순간적인 밸류 획득과 더불어 에너지와 드로우를 통해 사기를 치는 게 목적인 카드. 그러나 이미 구체 영창 자체에 에너지와 드로우를 적잖게 소모하고, 턴도 꽤 소모하는 편이라 강화해도 별로 좋지 않다는 의견이 결코 적지 않다.','Fission.JPG'),
(null,'Hello World','1','선천성 부여','특별','Defect','파워','턴 시작 시 무작위 일반 카드를 1장 얻습니다.','대부분의 덱에서는 무작위 카드를 얻는다곤 하나 일반 카드인 데다 강화도 안 되어있어 성능이 뛰어나진 않다. 경우에 따라 도움이 되는 카드가 나와 위기 대처에 도움이 될 수도 있지만, 덱의 매수만 늘려 방해만 되는 경우도 많다.','Hello World.JPG'),
(null,'반복','1','2회 발동','특별','Defect','파워','턴 시작 시 가장 오른쪽 구체의 지속 효과를 발동시킵니다.','축전기가 빈 슬롯을 더해준다면, 반복은 슬롯 수를 늘리진 않는 대신 이미 구체가 들어있는 슬롯을 제공해주는 것과 유사한 효과를 낸다. 특히 암흑 구체는 필요한 피해량을 빠르게 누적시키는 데 상당한 도움을 준다. 플라즈마 구체와도 궁합이 좋으며 냉기와 함께 사용할 때에는 안정성을 높여준다.','Loop.JPG'),
(null,'방열판','1','카드 뽑기(1→2)','특별','Defect','파워','파워 카드를 낼 때마다 카드를 1장 뽑습니다.','초반에는 메아리나 조각모음 같은 훨씬 중요한 파워 카드들이 많기 때문에 방열판 같은 간접 효과를 주는 카드로 덱을 늘리는 건 손해가 크다. 반면 폭풍 등을 다수 채용한 파워 위주의 전기 덱, 창의적인 인공지능을 채용한 덱 등은 생성된 모든 파워 카드에 드로우를 추가해주므로 이 때에는 큰 도움이 된다.','Heatsinks.JPG'),
(null,'자가 수리','1','회복량(7→10)','특별','Defect','파워','전투 종료 시 체력을 7 회복합니다.','아이언클래드의 유물을 그대로 들고 온 듯한 고효율 체력 회복 카드. 강화 후 메아리나 증폭을 받으면 무려 20을 회복할 수 있다. 첨탑 등반 시의 안정감을 상당히 높여주며, 커피 드리퍼 등의 유물도 고려해볼 수 있게 해주기에 한 장 정도는 채용하는 것도 좋다.','Self Repair.JPG'),
(null,'정전기 방전','1','영창(1→2)','특별','Defect','파워','공격 피해를 받을 때마다 전기를 1번 영창합니다.','여러 장 집었을 때나 연타 공격을 해오는 적에게 효율이 아주 높다. 영체화 카드 혹은 토리이 유물이 있다면 큰 피해 없이 정전기 방전만으로도 적에게 엄청난 피해를 입힐 수 있다. 문제는 방어도로 피해를 막을 때는 발동하지 않는다는 것. 일반적으로는 방어도로 피해가 들어오지 않게 하는 것이 기본 전술이기에 기껏 써놔도 발동하기 힘들 수 있다.','Static Discharge.JPG'),
(null,'조각모음','1','밀집(1→2)','특별','Defect','파워','밀집을 1 얻습니다.','소비, 인지 편향과 더불어 밀집을 제공하는 삼대장 카드. 플라즈마 외의 구체를 주력으로 사용하는 덱에서는 필수적이다. 가뜩이나 밀집 카드는 세 개밖에 없고, 다른 카드들은 강한 페널티를 가하기에 저코스트에 가볍게 밀집을 증가시킨다는 점에서 가치가 높다.','Defragment.JPG'),
(null,'축전기','1','구체 슬롯(2→3)','특별','Defect','파워','구체 슬롯 2개를 얻습니다.','카드로서는 유일하게 구체 슬롯을 늘려준다. 발현 효과와 지속 효과 중 어느 것을 주로 쓰느냐, 그리고 구체를 많이 채울 수 있느냐에 따라서 가치가 달라지는 카드. 발현 효과 위주로 쓰는 덱일 경우 과도한 구체 슬롯은 오히려 독이 된다.','Capacitor.JPG'),
(null,'폭풍','1','선천성 부여','특별','Defect','파워','파워 카드를 사용할 때마다 전기를 1번 영창합니다.','모든 파워 카드에 전기 영창 능력을 부여하는 카드. 특성상 다른 파워 카드보다 무조건 먼저 나와야 효과를 발휘하므로 강화 의존도가 높다. 예열 과정에서 딜을 축적할 수 있게 도와주므로 디펙트의 딜레마를 어느 정도 해소해주지만, 반대로 예열 과정 자체는 더 늘어지므로 일장일단이 있다.','Storm.JPG'),
(null,'기계학습','1','선천성 부여','희귀','Defect','파워','턴 시작 시 카드를 1장 더 뽑습니다.','효과 자체는 나쁘지 않으나 응용할 구석이 없어 심심한 카드. 다른 캐릭터의 경우에도 다음 턴부터 드로우를 한 장씩 추가해주는 카드는 리턴을 얻기까지 시간이 오래 걸린다는 점에서 그다지 고평가받지 않는데, 가뜩이나 예열에 오랜 시간이 필요한 디펙트의 특성상 여유가 없어 희귀 카드임에도 일반적으로는 버려진다.','Machine Learning.JPG'),
(null,'메아리의 형상','3','휘발성제거','희귀','Defect','파워','매 턴 처음 사용하는 카드가 두 번 사용됩니다.(휘발성)','그 강력한 네크로노미콘의 효과를 모든 카드에 상시로 적용받을 수 있다. 형상류 카드답게 높은 코스트를 요구하지만, 메아리의 형상은 한 장 깔아두면 상당한 범용성을 특징으로 밸류를 뻥튀기하는 데 큰 도움을 준다.','Echo Form.JPG'),
(null,'버퍼','2','막기 횟수(1→2)','희귀','Defect','파워','다음 번에 체력을 잃는 것을 막아줍니다.','후반부 적의 강력한 공격에 대응하여 안정적인 운영이 가능해진다. 과도자, 첨탑의 방패, 심장 등의 강한 단타 등을 효율적으로 막아준다.','Buffer.JPG'),
(null,'전기 역학','2','영창 횟수(2번→3번)','희귀','Defect','파워','전기구체가 적 전체를 타격합니다. 전기를 2번 영창합니다.','희귀 카드 중에서도 획득 우선 순위가 높은 카드. 다수의 적을 상대로 전기 구체의 효율을 크게 높여주며, 공격 대상을 지정할 수 없어 원하는 적 하나에 피해를 집중시키기 어려운 전기 구체의 문제점을 극적으로 축소시켜준다.','Electrodynamics.JPG'),
(null,'창의적인 인공지능','3','비용(3→2)','희귀','Defect','파워','매 턴 시작 시 무작위 파워 카드를 1장 얻습니다.','파워를 무한대로 제공해 주는 강력한 카드. 제대로 사용할 경우 보스를 쉽게 클리어하는 데 도움을 준다. 보스를 잡을 만한 결정력이 없는 경우 채용하면 덱의 성장성을 상당히 높여줄 수 있다. 아이언클래드의 악마의 형상과 유사한 카드.','Creative AI.JPG'),
(null,'인지 편향','1','밀집(4→5)','희귀','Defect','파워','밀집을 4 얻습니다. 내 턴 시작 시 밀집을 1 잃습니다.','높은 수치의 밀집을 단숨에 제공해주는 강력한 카드. 발동 이후 4턴(5턴) 동안은 이득을 볼 수 있으니 단기전을 노리는 구체 덱에 좋다. 특히 고승천으로 갈수록 요구되는 전투의 템포가 빨라지기 때문에 그 가치가 더 높아진다. 다만 밀집이 0 미만으로도 감소할 수 있기 때문에 장기전이 된다면 오히려 독이 된다. 따라서 전투가 길어질 수 있는 보스전 등에서는 사용할 타이밍을 잘 재야 한다.','Biased Cognition.JPG'),
(null,'타격','1','피해량(6→9)','일반','Wather','공격','피해를 6 줍니다.','기본 공격 카드. 진노 상태에서는 1코스트 12(18)딜로 사용할 수 있어 타 직업 대비 제거 우선순위는 낮은 편. ','Strike_Wather.JPG'),
(null,'분화','2','카드 비용(2→1)','일반','Wather','공격','피해를 9 줍니다.진노합니다.','와쳐의 시작 덱에 포함되어 있는 카드. 강화 의존도가 매우 높다. 강화한다면 상당히 준수한 성능의 카드로, 대부분 와쳐의 덱이 진노/명상덱으로 귀결되는 원인 중 하나.','Eruption.JPG'),
(null,'관절 부수기','1','피해량(8→10), 취약 부여(1→2)','일반','Wather','공격','피해를 8 줍니다.이번 전투에서 직전에 사용한 카드가 스킬 카드일 경우 취약을 1 부여합니다.','와쳐의 몇 안되는 취약을 부여하는 카드. 스킬을 사용한 다음에 써야 취약이 걸리는데, 와쳐는 기본 유물이 첫턴부터 0코스트 보존 스킬 카드인 기적을 제공해주므로 매우 간편하게 사용할 수 있다. 진노와 시너지가 매우 좋다. 사용한다면 강화는 필수적.','Crush Joints.JPG'),
(null,'뒤처리','1','피해량(7→11)','일반','Wather','공격','피해를 7 줍니다.이번 전투에서 직전에 사용한 카드가 공격 카드일 경우, 1 에너지를 얻습니다.','전사의 드롭킥과 비슷한 조건부 노코스트 공격카드. 피해도 나쁘지 않고, 공짜가 되는 조건이 쉬워 사용할 만 하다. 단, 카드의 잠재력이 높은 것은 아니다. 와처의 대부분 카드들이 코스트보단 패순환이 부족한 경우가 많아서, 보통은 채용되지 않는다.','Follow Up.JPG'),
(null,'벨트 휘두르기','1','피해량(8→10), 약화 부여(1→2)','일반','Wather','공격','피해를 8 줍니다.이번 전투에서 직전에 사용한 카드가 공격 카드일 경우, 약화를 1 부여합니다.','와쳐의 몇 안되는 약화를 부여하는 카드. 관절 부수기와 비슷하다. 베타아트가 가관인데, 와쳐가 장난스런 표정을 지으며 허리띠로 사일런트의 엉덩이를 찰싹 때리고 있고 사일런트는 화들짝 놀란 표정을 하고 있다.','Sash Whip.JPG'),
(null,'요행','0','예지(1→2), 획득 방어도(2→3), 피해량(3→4)','일반','Wather','공격','예지 1.방어도를 2 얻습니다.피해를 3 줍니다.','무자원 잡탕카드. 예지 1장으로는 이득을 보는 경우는 많지 않다. 게다가 단순히 딜, 방어 카드로 봐도 밸류가 너무 낮아 덱 압축만 방해하는 꼴이라 쓰기 힘들다. 단식을 사용하는 덱이 가끔 채용.','Just Lucky.JPG'),
(null,'운명 가르기','1','피해량(7→9), 예지(2→3)','일반','Wather','공격','피해를 7 줍니다.예지 2.카드를 1장 뽑습니다.','덱 조작 후 드로우를 하는 것이므로 좋다. 자체 공격력은 낮은 편.','Cut Through Fate.JPG'),
(null,'일진광풍','0','피해량(4→6)','일반','Wather','공격','피해를 4 줍니다.경지를 바꿀 때마다 버린 카드 더미에서 손으로 가져옵니다.','경지가 바뀌면 손으로 들어오는 공짜 딜카드. 그러나 추월을 기반으로 하는 명상-진노 순환덱이 유행하면서 오히려 평가가 급락했다. 와쳐는 더 이상 이런 카드 없어도 진노 딜카드를 여러번 우려먹는 형식으로 게임을 풀어나갈 수 있게 되었기 때문. 완전히 초반용으로 집기에는 노강화 딜이 너무 낮아 차라리 시너지를 아예 고려하지 않고 쓸 수 있는 다른 딜 카드가 더 좋은 편이다.','Flurry Of Blows.JPG'),
(null,'정권 수양','1','피해량(9→14)','일반','Wather','공격','피해를 9 줍니다.현재 경지에서 벗어납니다.','지속된 버프로 1코 치고는 깡딜이 꽤 세진 카드. 보통 급하게 진노에서 벗어나면서 딜을 넣는 용도. 강화시 피해가 5 늘어나서, 1코 치고는 강화 효율이 좋은 편.','Empty Fist.JPG'),
(null,'축성','0','피해량(5→8)','일반','Wather','공격','적 전체에게 피해를 5 줍니다.','도적 카드 칼질(단일 적 대상 6 피해)의 상위호환이다. 자체 밸류는 좋지 않으나, 진노 상태에서 사용하면 광역 10(16)피해이므로 쓸만한 편.','Consecrate.JPG'),
(null,'충돌 강타','1','피해량(7→10)','일반','Wather','공격','전투 중인 적 하나 당 7 의 피해를 입힙니다.','초반을 넘기는데 좋은 카드. 적이 2명 이상일때 진노 상태라면 1코 치고는 매우 강력한 딜을 넣을 수 있다. 다만 사일런트의 파멸과 유사하게 보스전이나 후반에는 비교적 약해진다. 적이 3명 이상 있을 때 이 카드를 쓰면 볼링 치는 소리가 난다.','Bowling Bash.JPG'),
(null,'흩날리는 소매','1','피해량(4→6)','일반','Wather','공격','피해를 4 만큼 2 번 줍니다.(보존)','도적의 단검 분사가 단일 적 대상으로 바뀐 대신 보존이 붙은 카드. 딜 자체가 낮아 초반을 견디는 데 쓰고 다른 딜카드를 더 찾아보는게 좋다.','Flying Sleeves.JPG'),
(null,'개인기','2','피해량(30→40)','특별','Wather','공격','이 카드가 손에 있는 유일한 공격 카드일 경우에만 사용 가능합니다. 피해를 30 줍니다.','전사의 격돌을 뒤집은 듯한 카드. 상태이상/저주 때문에 못 쓰는 일은 없지만, 코스트가 높아 다른 공격카드를 다 쓴 뒤에는 이 카드를 쓸 코스트가 모자란 경우가 있다. ','Signature Move.JPG'),
(null,'격분','1','공격 횟수(3→4)','특별','Wather','공격','피해를 3 만큼 3 번 줍니다.진노합니다.이 카드를 뽑을 카드 더미에 섞어넣습니다.','진노에 돌입하는 용도로도 괜찮고, 다중 타격이기 때문에 힘증가, 손절, 불의 고리와도 시너지가 좋다. 강화 시 연타 횟수가 늘어나는 것 또한 장점. 다만, 덱으로 돌아가는 효과 특성상 딱 한 장 정도가 좋다. 추월 파워를 쓴 상태로 격분을 사용하면 2장 드로우 효과로 방금 덱에 돌아간 격분이 다시 손에 잡힐 수 있다.','Tantrum.JPG'),
(null,'결론','1','피해량(12→16)','특별','Wather','공격','적 전체에게 피해를 12 줍니다.턴을 종료합니다.','말 그대로 턴을 마무리 지을 때 쓸만한 카드. 1코스트 치고는 굉장히 높은 깡딜에 광역 피해를 지니고 있으나, 턴 종료가 거슬려 쓰기 힘든 카드. 무엇보다도, 진노 상태에서 사용하기 힘들기 때문에 일반 카드인 축성보다도 못한 경우가 많다. 정제된 혼돈과 같이 사용하면 의문사를 당하는 경우가 있으니 주의.','Conclude.JPG'),
(null,'돌려차기','2','피해(15→20)','특별','Wather','공격','피해를 15 줍니다.카드를 2 장 뽑습니다.','천적의 즉발 드로우 버전. 코스트가 높지만 딜 자체도 나쁘지 않고 와쳐는 즉발 드로우 카드가 적으므로 추월이 아직 없다면 채용할 만 하다. 그 외 강림 덱에서도 좋은 편.','Wheel Kick.JPG'),
(null,'맹공','2','피해량(9→12)','특별','Wather','공격','피해를 9 줍니다.막히지 않은 피해량만큼 방어도를 얻습니다.','전사의 사신 방어도 버전. 그냥 사용하면 2코스트 9방어도 9피해로, 도적의 돌진과 비슷하다. 진노로 피해가 2배가 되면 획득 방어도도 2배가 되어 좋다. 너프 이후로 딜링기로 쓰기에는 적합하지 않다. 강화 기준 진노 상태에서 24딜에 24방어도를 챙겨주는 공방일체의 안정적인 카드지만 적의 방어도에 공격이 일부 막히거나, 체력이 적은 적을 마무리하는 데 활용했거나, 약화에 걸려 공격력이 떨어지거나 하면 얻는 방어도도 감소하니 신경 쓸 필요가 있다. 특히 불가침을 사용하는 네메시스를 상대로 패가 꼬이면 저주나 다름없어지기도 한다.','Wallop.JPG'),
(null,'무외','1','피해량(8→11)','특별','Wather','공격','피해를 8 줍니다.적이 공격할 의도가 있다면 명상합니다.','디펙터의 눈 할퀴기와 비슷한 적의 의도 관련 조건부 카드. 상대가 공격 의도가 있다면 방어를 위해 진노에서 벗어나야 하는 상황인데, 덤으로 명상까지 시켜준다. 조건 충족을 못 해도 1코 8딜이라는 것 자체가 타격보다 밸류가 높고, 조건부이긴 해도 부가효과까지 달린 1코 명상이라는 점에서 집을 가치가 충분한 카드. 흔히 진노-명상 사이클덱을 구성하는데 기본요소. 기본덱에 주어지는 분화에 파워카드 추월이 갖춰진 상태라면 덱 압축만으로도 탑을 오르기가 쉬워진다.','Fear No Evil.JPG'),
(null,'손절','1','피해량(5→7), 획득 방어도(2→3)','특별','Wather','공격','피해를 5 줍니다.해당 적을 공격할 때마다 방어도를 2 얻습니다.(소멸)','공격 카드처럼 보이지만 수비 용도로 훨씬 많이 쓰는 카드. 마음의 요새와 함께 와쳐의 핵심 방어도 플랜이다.격분과 라그나로크 등 연타류 공격에는 연타 횟수만큼 중첩되어 방어도를 얻는다. 특히 와쳐의 약점인 심장전 난이도를 크게 낮춰준다.','Talk to the Hand.JPG'),
(null,'시간의 모래','4','피해량(20→26)','특별','Wather','공격','피해를 20 줍니다.보존될 때마다 이번 전투 동안 이 카드의 비용이 1 만큼 줄어듭니다.(보존)','주는 피해도 나쁘지 않고, 보존 카드라 사용 타이밍 잡기도 편하다. 0코스트로 사용하면 매우 좋지만, 4턴씩이나 손에 가지고 있기엔 애매하므로 2~3번만 보존시켜서 1~2코스트로만 사용해도 상당히 좋다.','Sands of Time.JPG'),
(null,'천국의 계단','2','피해량(10→15)','특별','Wather','공격','피해를 10 줍니다.뽑을 카드 더미에 무력 돌파를 1 장 섞어 넣습니다.','2코 10딜이라는 형편없는 카드이지만, 좋은 깡딜카드를 섞어넣는다. 토큰 카드까지 뽑아 쓰면, 2코스트로 피해를 30(35) 넣을 수 있다. 하지만 결국 2드로우나 써야해서 덱 압축을 방해한다는 점 때문에 좋지 않다. 게다가 와쳐는 이런 카드 안 써도 피해 잘 넣는다.','Reach Heaven.JPG'),
(null,'현실 개척','1','피해량(6→10)','특별','Wather','공격','피해를 6 줍니다.엄벌을 1 장 얻습니다.','천국의 계단과 비슷한 카드. 그래서 안 쓰인다. 엄벌 카드를 덱에 넣는 게 아니라 바로 손으로 가져온다는 점에서 천국의 계단보다는 낫다. 문제는 토큰 카드 엄벌이 1코스트를 먹는다는 것. 총 2코스트 18(22)딜짜리 카드인데 밸류가 좋다고 보기 어려워 잘 쓰이지 않는다.','Carve Reality.JPG'),
(null,'회오리 타격','2','피해량(7→10), 피해 증가량(4→5)','특별','Wather','공격','피해를 7 줍니다.보존될 때마다 이번 전투 동안 이 카드의 피해량이 4 만큼 증가합니다.(보존)','시간의 모래와 비슷한, 보존할수록 강해지는 카드. 이론상 잠재력은 무한하지만 쓸만해질 때까지 너무 오래 걸린다. 시간의 모래를 2턴 정도 보존하고 쓰는 딜(2코 20딜)과 코스트가 회오리타격 2~3턴 보존해두고 쓰는 것(2코 19딜)보다 훨씬 낫다.','Windmill Strike.JPG'),
(null,'횡행','0','피해량(4→6)','특별','Wather','공격','피해를 4 줍니다.예지할 때마다 이 카드를 버린 카드 더미에서 손으로 들고 옵니다.','일진광풍과 비슷한 컨셉의 카드. 이 카드가 예지되어서 버려졌을 때도 패로 바로 들어온다. 깡딜도 워낙 낮고, 예지 컨셉을 지원하는 카드가 많지 않아 쓰기는 힘든 편.','Weave.JPG'),
(null,'교훈','2','피해량(10→13)','희귀','Wather','공격','피해를 10 줍니다.치명타라면, 덱에 있는 무작위 카드를 영구히 강화합니다.(소멸)','몇 안되는 치명타 효과 카드. 전사의 전투장비, 무색 신성 카드처럼 이번 전투에만 적용되는 것이 아니라, 영구적으로 카드를 강화한다. 키 카드를 강화해 준다면 모닥불에서 강화 대신 휴식을 누를 수도 있다. 반대급부로 코스트나 공격력 등 전투 효과는 최악 수준. 사용하기 어려운 카드이다.','Lesson Learned.JPG'),
(null,'광휘','1','피해량(12→16)','희귀','Wather','공격','피해를 12 줍니다.이번 전투에서 얻었던 만트라만큼 추가 피해를 줍니다.','만트라 쌓는게 필연적인 강림덱에서 쓰는 카드. 기본 공격력이 상당해서 같이 나온 카드 중 쓸만한게 없고, 딜카드가 필요한 초반에 무난하게 집어볼만한 깡딜카드. 만트라를 쌓기 시작하면 어느 공격카드보다 우월해지는데, 만트라 10쌓고 강림하면 26*3데미지로 78데미지를 먹여줄 수 있다.','Brilliance.JPG'),
(null,'라그나로크','3','피해량(5→6), 공격 횟수(5→6)','희귀','Wather','공격','무작위 적들에게 피해를 5 만큼 5 번 줍니다.','전사의 몽둥이질처럼 단순하며 무겁고 강한 카드. 몽둥이질보다는 시너지 효과를 일으키기 편하다.단순히 3코스트 밸류를 단독으로만 보면 떨어져 보이지만, 진노/강림덕에 실질 화력이 높다.','Ragnarok.JPG'),
(null,'수비','1','방어도(5→8)','일반','Wather','스킬','방어도를 5 얻습니다.','와쳐의 기본 카드. 다른 캐릭터보다 제거 우선순위가 좀 높은 편.','Defend_Wather.JPG'),
(null,'경각','2','획득 방어도(8→12)','일반','Wather','스킬','방어도를 8 얻습니다.명상합니다.','와쳐의 기본 카드. 명상 때문인지 밸류가 심하게 낮다. 심지어 모든 캐릭터가 가진 저밸류 카드인 수비 두 장보다 얻는 방어도가 적다. 강화해도 영 믿음직스럽지 못하다. 다만 안정적인 명상 카드들이 대부분 전투 효과가 없는데 반해 경각은 전투 효과를 가지긴 한다는 장점이 있다. 썩어도 명상 카드라 제거 우선순위는 의외로 낮은 편.','Vigilance.JPG'),
(null,'고요함','1','비용(1→0)','일반','Wather','스킬','명상합니다.(보존)(소멸)','1회용으로 타이밍 재서 명상하는 카드. 주로 진노 카드가 명상 카드에 비해 너무 많을 때, 진노에서 1회성으로나마 탈출하고 코스트 사기를 치기 위해 채용된다.','Tranquility.JPG'),
(null,'급소','1','표식 부여량(8→11)','일반','Wather','스킬','표식 을 8 부여합니다. 모든 적의 체력을 대상에게 부여된 표식 만큼 감소시킵니다.','와쳐의 주요 컨셉인 경지순환과 동떨어져 쓰기 어려운 카드. 진노/강림 피해 배수 뻥튀기도 적용되지 않는다. 디버프 판정이라 인공물에 막히는 것도 까다로운 점. 효과 자체는 준수한데, 정작 와쳐에게 이 카드를 지원하는 카드가 없다. 표식이라는 키워드 자체가 이 카드 단 한 장이고 버프/디버프의 영향도 받지 않는다. 따라서 급소를 본격적으로 활용하려면 아예 급소만을 중점으로 둔 덱을 구성해야한다.','Pressure Points.JPG'),
(null,'백열','1','비용(1→0)','일반','Wather','스킬','진노합니다.(보존)(소멸)','고요함의 진노 버전. 진노는 명상만큼 보존하면서까지 타이밍을 찾을 가치가 없다. 게다가 진노 카드면서 딜을 안 넣는다는 점도 아쉬운 점. 고요함보다 잘 안 쓰이는 편.','Crescendo.JPG'),
(null,'보호','2','방어도(12→16)','일반','Wather','스킬','방어도를 12 얻습니다.(보존)','2코스트 방어카드. 두 차례 너프 후 성능이 지나치게 낮아졌다. 지배와의 시너지는 좋다.','Protect.JPG'),
(null,'부복','0','획득 만트라(2→3)','일반','Wather','스킬','만트라를 2 얻습니다.방어도를 4 얻습니다.','사일런트의 막기의 와쳐 버전. 강림 덱이 좀 미묘하기도 하고, 카드의 밸류도 낮아 이래저래 애매한 카드. 강림 덱에서 헌신을 구하지 못했다면 숭배와 함께 한 장 정도 사용해볼 만한 카드다.','Prostrate.JPG'),
(null,'육체 수양','1','방어도(7→10)','일반','Wather','스킬비','방어도를 7 얻습니다.현재 경지에서 벗어납니다.','수양 시리즈의 방어 버전. 정권수양과 달리 방어도 수치가 너무나도 애매하다. 수양 시리즈 중 가장 인기가 없다.','Empty Body.JPG'),
(null,'저지','0','획득 방어도(3→4), 추가 획득 방어도(9→14)','일반','Wather','스킬','방어도를 3 얻습니다.진노 중일 경우 방어도를 추가로 9 얻습니다.','기본적인 방어도는 몹시 불량하지만, 진노할 때 가치가 상승하므로 쓸만한 카드. 진노 중일 경우, 12(18)의 방어도를 0코스트 카드로 얻는다. 민첩 효과를 2배로 받는 것도 장점. 마음의 요새나 손절보다 얻기 쉽고, 쓰기 쉽다는 장점 덕에 진노-명상 덱에서 방어도 플랜 B로 운용된다.','Halt.JPG'),
(null,'제 3의 눈','1','방어도(7→9), 예지(3→5)','일반','Wather','스킬','방어도를 7 얻습니다.(예지3)','무난한 1코스트 방어 카드. 덱 조작 능력 덕에 안정성을 꽤 높여준다. 방어도 상승도가 낮아 흘려보내기마냥 아무때나 막 고를 카드는 아니다.','Third Eye.JPG'),
(null,'평가','1','방어도(6→10)','일반','Wather','스킬','방어도를 6 얻습니다. 뽑을 카드 더미에 통찰을 섞어 넣습니다.','드로우를 돕는 카드를 한 장 섞어넣고 방어도를 올려주는 카드. 강화 효율이 좀 높다. 통찰까지 고려하면 카드 2장을 써서 2장을 뽑으므로 패 소모 없이 에너지 하나만 써서 방어도를 얻는 셈. 현실 지배로 통찰이 강화되면 오히려 패가 하나 늘어난다. 통찰이 뽑혀야 패 이득을 볼 수 있지만, 보존 덕에 아껴뒀다 필요할 때 사용할 수 있어 의외로 좋은 카드.','Evaluate.JPG'),
(null,'기도','1','만트라(3→4)','특별','Wather','스킬','만트라를 3 얻습니다.뽑을 카드 더미에 통찰을 1 장 섞어 넣습니다.','주는 만트라는 0코스트인 부복보다 고작 1 높은 주제에, 전투 효과라곤 즉발도 아니고 어드밴티지도 없는 드로우밖에 없어 나쁜 카드. 이전에는 심지어 희귀카드이기도 했다.','Pray.JPG'),
(null,'내면의 평화','1','뽑는 카드(3장→4장)','특별','Wather','스킬','명상 중이라면 카드를 3 장 뽑습니다. 그렇지 않으면 명상합니다.','명상으로 무한 순환할 때, 정신수양만으로 돌리기 뻑뻑할 경우 쓸 수 있다. 그냥 코스트가 넘칠 때도 쓸 수 있지만, 천신의 형상 + 명상이 아니라면 그런 식의 활용은 흔하지 않다. 무외와 함께 단 둘뿐인 1코스트 명상 카드인데다가 와처에게 희귀한 즉발 드로우 카드이므로 채용가치는 충분하다.','Inner Peace.JPG'),
(null,'들끓는 울화','1','뽑는 카드(2장→3장)','특별','Wather','스킬','다음 턴 시작 시 진노하고 카드를 2 장 뽑습니다.','다음 턴에 진노와 추가 드로우를 예약해놓고, 미리 명상을 건 다음 진노할 수 있어 꽤 괜찮은 카드. 다만 다음 턴 같은 거 없이 당장 진노하는 덱, 진노와 명상을 무한 순환하는 덱이라면 채용을 피하자.','Simmering Fury.JPG'),
 (null,'분개','1','취약(3→5)','특별','Wather','스킬','진노 중이라면 적 전체에게 취약을 3 부여합니다. 그렇지 않으면 진노합니다.','3턴씩이나 가는 광역 취약은 굉장히 희귀하고 밸류 높은 효과라 좋다. 진노를 거는 카드로는 적합하지 않지만 아쉬운 대로 써먹을 수는 있다.','Indignation.JPG'),
(null,'불의 고리','1','피해량(5→8)','특별','Wather','스킬','다음에 사용하는 공격 카드의 피해량이 5 증가합니다.','한 번의 공격에 한해 존재하는 힘을 부여하는 효과. 진노, 강림, 다중 타격 카드와 시너지가 좋다. 이 카드 자체는 보존이 없으나, 버프는 보존된다. 그러나 와쳐는 굳이 이 카드가 없어도 딜이 충분해서 넣을 자리가 없다. 라그나로크나 격분과의 시너지는 훌륭하기 때문에, 드로우 수단이 풍부해 시너지를 노리기 좋은 상황이라면 집어볼만하다.','Wreath of Flame.JPG'),
(null,'사색','1','가져오는 카드(1장→2장)','특별','Wather','스킬','버린 카드 더미에서 카드 1 장을 손으로 들고 온 뒤 보존합니다.명상합니다.턴을 종료합니다.','상당히 특이한 카드. 디펙트의 홀로그램처럼 쓴 카드를 우려먹는데 사용할 수 있다. 원래 보존하지 않는 카드에게 보존 시너지를 볼 수 있게 해주고, 강화 시 시너지가 있는 두 카드를 확정적으로 손에 쥘 수 있게 해주는 점도 장점. 특히 보존된 카드의 코스트를 이번 전투 동안 감소시키는 지배 파워카드와의 궁합은 최상급. 다만 턴 강제 종료 때문에, 순환덱에서는 쓰지 않는다.','Meditate.JPG'),
(null,'인내','1','획득 방어도(5→7), 방어도 증가(2→3)','특별','Wather','스킬','방어도를 5 얻습니다.보존될 때마다 이번 전투 동안 이 카드의 방어도가 2씩 증가합니다.(보존)','보존만 오래하면, 한 장에 고방어도를 얻을 수 있는 나쁘지 않은 카드.','Perseverance.JPG'),
(null,'장법','1','약화(1→2)','특별','Wather','스킬','이번 턴 동안 방어도를 얻을 때마다 적 전체에게 약화를 1 만큼 부여합니다.','기본 1코로 장법 사용 이후 추가로 방어도를 적립할 때마다 광역 약화를 부여하는 굉장히 강력한 카드. 와처는 마음의 요새나 손절 등의 자잘한 방어도를 많이 쌓는 형식의 방어법을 구사하기 때문에 한번 시전으로 여러 턴의 약화효과를 가져갈 수 있다. 와쳐의 스킬카드의 방어력이 불안하기 때문에 작정하고 이 카드로 많은 약화를 쌓아서 피해를 줄이는 전략은 효과적이다.','Wave of the Hand.JPG'),
(null,'정신 수양','1','뽑는 카드(2장→3장)','특별','Wather','스킬','카드를 2 장 뽑습니다.현재 경지에서 벗어납니다.','순수 명상 순환덱 핵심 카드. 명상에서 벗어나서 코스트를 얻으면서 덱을 순환시킨다. 워낙 순수 명상 순환덱이 강해서 뽑는 카드의 양이 너프되었지만, 그래도 잘 쓰인다.','Empty Mind.JPG'),
(null,'존엄','1','방어도(6→9)','특별','Wather','스킬','방어도를 6 얻습니다.이번 전투에서 직전에 사용한 카드가 스킬 카드일 경우, 카드를 2 장 뽑습니다.','조건부 공중제비. 요구하는 조건도 그렇게 거슬리지 않는 편. 기본 유물로 얻는 기적 1장이 스킬 카드 취급이라 여차하면 기적 존엄으로 바로 드로우부터 봐도 된다. 특히 천신의 형상을 사용할 때는 거의 손해가 없다시피한 카드라 한두장은 무조건 집게 된다. 단점이라면, 방어도를 보고 쓰기엔 사일런트와 달리 민첩 쌓기가 어렵다는 점 정도.','Sanctity.JPG'),
(null,'현실 속이기','1','방어도(4→7)','특별','Wather','스킬','방어도를 4 얻습니다.안전을 손으로 가져옵니다.','2코스트 16방어로만 봐도 무난하다. 민첩의 영향도 더 잘 받고, 안전은 보존 덕에 사용 타이밍 잡기가 수월하다. 무난한 방어카드.','Deceive Reality.JPG'),
(null,'수집','X','다음 지속 턴(X→X+1)','특별','Wather','스킬','다음 X 턴 동안 내 턴 시작 시 손으로 기적+를 가져옵니다.(소멸)','이번 턴을 팔아 이후 턴을 도모한다는 점에서 도플갱어와 비슷하지만, 더 좋은 카드이다.','Collect.JPG'),
(null,'숭배','2','보존추가','특별','Wather','스킬','만트라를 5 얻습니다.','덱 완성이 안되었을 때 나타나면 집어볼만한 카드. 딱 두번만 쓰면 강림하므로, 세 번 이상 쓸 것이 강요되는 다른 만트라 카드보다 리턴이 빠르다. 강화시 보존이 추가되어 사용 타이밍 잡기도 편해져 강림덱 핵심 카드가 된다. 보존 카드들이 코스트 사기를 치게 해주는 지배와의 시너지는 최상급. 만트라 외에는 다른 효과가 없다는 게 흠이다.','Worship.JPG'),
(null,'외세','0','이번 턴 동안 가져온 카드의 비용이 0이 됨','특별','Wather','스킬','모든 직업의 공격 카드 셋 중 하나를 선택해 얻습니다.(소멸)','이런 종류의 카드들이 그렇듯이 상상도 못한 사기를 칠 수 있게 도와준다. 물론 사기도 잘 치지만, 90%의 경우 타격에 버금가는 함정카드도 자주 나타나는 카드.','Foreign Influence.JPG'),
(null,'회전','2','방어도(8→11)','특별','Wather','스킬','방어도를 8 얻습니다.다음에 사용하는 공격 카드의 비용이 0이 됩니다.','코스트 이득을 볼만한 공격 카드는 라그나로크밖에 없고, 획득하는 방어도도 불량한 카드. 방어도 8(11)은 아이언클래드 1코스트 카드 흘려보내기와 동급이다.','Swivel.JPG'),
(null,'데우스 엑스 마키나','-','획득 기적(2장→3장)','희귀','Wather','스킬','이 카드를 뽑으면 소멸하면서 기적을 2 장 얻습니다.(사용불가)','뽑는 즉시 에너지 카드를 추가해주며, 기적은 보존되므로 필요한 타이밍에 사용할 수 있다. 현실 지배나 손패 강화 포션과 조합하여 6에너지를 더하는 코스트 사기도 칠 수 있다. 에너지가 넘친다면 꼭 필요하지는 않다. 와쳐는 이 카드 외에도 에너지 사기 수단이 많고 이 카드 때문에 손패가 터지는 일도 종종 있어서 좀 애매한 카드.','Deus Ex Machina.JPG'),
(null,'마술 칼날','X','말살검의 피해횟수(X번→X+1번)','희귀','Wather','스킬','뽑을 카드 더미에 말살검을 섞어 넣습니다.(소멸)','마술 칼날 카드를 사용하면 사용 시의 코스트 값이 X에 적용된 말살검이 생성된다. 예를 들어 3코스트 상황에서 마술칼날 카드를 사용하면 말살검(1코스트. 피해를 9 만큼 3 번 줍니다.) 카드를 뽑을 카드 더미에 섞어넣게 된다. 마술칼날+라면 4번짜리 말살검이 생성된다. 토큰 카드 말살검은 X코스트 카드가 아님에 유의.','Conjure Blade.JPG'),
(null,'신성 모독','1','보존 추가','희귀','Wather','스킬','강림합니다.다음 턴에 죽습니다.(소멸)','당장 이번 턴에 킬각을 내는 용도의 카드. 피해 3배, 에너지 +2의 효과를 얻으므로 일반 타격들만 동원해도 킬각을 보기 쉽다. 강화시 보존이 추가되어 킬각 재기가 더 편해진다.','Blasphemy.JPG'),
(null,'심판','1','즉사시킬 적의 체력제한(30→40)','희귀','Wather','스킬','적의 체력이 30 이하일 경우 체력을 0으로 만듭니다.','조건부 1코스트로 40딜 카드로 봐도 무방하다. 구체형 수호기 잡기 딱 좋은 카드로, 그게 아니라도 초반 넘기기에 특효약이다. 2막까지는 엄청난 효율을 자랑하나 3막부터는 폭발기나 파충류 주술사의 소환물을 걷어내는 정도 외에는 딱히 활약하지 못하고, 4막에서는 아예 저주 수준인지라 덱에서 제거해버리는 경우가 많다.','Judgment.JPG'),
(null,'알파','1','선천성 추가','희귀','Wather','스킬','뽑을 카드 더미에 베타를 섞어 넣습니다.(소멸)','수비만 하면서 오메가의 강력한 성능으로 적을 쓰러트리는 컨셉의 카드. 이 카드를 쓰려면 6에너지, 3드로우를 해야 한다.사실상 폭탄의 하위호환.','Alpha.JPG'),
(null,'염원','3','얻는 판금 갑옷(6→8), 얻는 힘(3→4), 얻는 골드(25→30)','희귀','Wather','스킬','하나 선택:판금 갑옷을 6 얻거나, 힘을 3 얻거나, 골드를 25 얻습니다.(소멸)','세 개의 소원 중 하나를 선택하면 된다. 판금 갑옷을 얻는 것은 영생, 힘을 얻는 것은 전능, 골드를 얻는 것은 부와 명예이다. 잡몹전에서는 골드를 얻고, 보스전 같은 중요한 전투에서는 판금 갑옷이나 힘을 얻는다. 문제는 턴 하나를 넘기는 코스트에 비해서 너무 애매한 성능.','Wish.JPG'),
(null,'영혼 방패','2','방어도(3→4)','희귀','Wather','스킬','손에 있는 카드 한 장 당 방어도를 3 얻습니다.','추가 드로우나 보존 카드로 패를 늘리고 써야 이득인 카드. 기본 드로우를 보고 바로 쓴다면, 12(16) 방어도를 얻는다. 기적 한 장을 포함하면 15(20)방어도. 기본적으로, 기적을 남기지 않는다면 희귀 방어 카드 치고는 뭔가 애매한 가성비를 지닌다. ','Spirit Shield.JPG'),
(null,'전지','4','비용(4→3)','희귀','Wather','스킬','뽑을 카드 더미에서 카드를 1 장 선택합니다.선택한 카드를 두 번 사용하고 소멸시킵니다.(소멸)','대개 어차피 소멸될 소멸카드나 파워카드를 대상으로 삼는다. 성능은 좋지만 너무 무거워 꺼려지는 천신의 형상과는 엄청난 궁합. 이런 류의 카드가 그렇듯 원래 카드 비용을 무시하므로 더 그렇다. 또한 덱이 전지가 2장 이상 있을 경우에 전지→전지 순으로 사용하면, 첫 번째 전지의 효과로 두 번째 전지를 2번 사용한다. ','Omniscience.JPG'),
(null,'회피 도약','3','비용(3→2)','희귀','Wather','스킬','이번 턴 후에 내 턴을 추가로 진행합니다.턴을 종료합니다.(소멸)','이 카드 사용시, 적의 턴을 일방적으로 무시한다. 이 점 때문에 와쳐의 최고성능 카드 중 하나로 꼽히며, 무조건 많이 집을수록 좋다. 단점은 적의 턴이 진행되지 않아 심장의 천하무적이 갱신되지 않고 네메시스의 불가침이 사라지지 않는다는 것 정도.','Vault.JPG'),
(null,'휘갈김','1','비용(1→0)','희귀','Wather','스킬','손이 가득 찰 때까지 카드를 뽑습니다.(소멸)','(손이 가득 찰 때까지 카드를 뽑는다 = 카드를 10장 뽑는다) 이다. 소멸을 제외한 드로우 성능은 이 게임의 다른 모든 드로우카드의 상위호환이다. 손해를 볼 가능성이 없다시피 하므로 보는 족족 집어도 상관없다. 강화 의존도가 높은 편이고 소멸이라는 점이 유일한 단점. 베타 시절에는 0코스트로 3장을 드로우하지만, 사용한 턴에는 스킬 카드를 쓸 수 없는 카드였다. 당연히 아무도 쓰지 않았고, 현재 효과로 변경되었다.','Scrawl.JPG'),
(null,'단식','2','힘(3→4), 민첩(3→4)','특별','Wather','파워','힘을 3 얻습니다.민첩을 3 얻습니다.매 턴 시작시 에너지를 1 적게 얻습니다.','힘, 민첩 증가 효과는 노강 기준 강화 발화 + 강화 발놀림이므로 사기급. 다만 페널티가 에너지 유물을 잃어버리는 급이라, 사용하기 망설여지는 카드. 해당 페널티를 쉽게 카운터치는 오렌지 알약이나 천신의 형상과 시너지가 좋다.','Fasting.JPG'),
(null,'마음의 요새','1','방어도(4→6)','특별','Wather','파워','경지를 바꿀 때마다, 방어도를 4 얻습니다.','대단히 강력한 방어 카드. 공격 카드 손절과 함께 와쳐의 대표적인 방어도 솔루션이며, 보라색 카드 가운데 가장 강력한 방어카드이다. 와쳐는 필연적으로 경지를 자유자재로 넘나드는 운용을 해야만 하는데 그 때마다 손상의 영향 없는 4(6) 방어도를 제공한다.','Mental Fortress.JPG'),
(null,'선견지명','1','예지(3→4)','특별','Wather','파워','내 턴 시작 시 예지 3','매 턴 드로우 하기 직전에 예지부터 한다. 다른 예지 카드들과 달리, 이 카드는 사용하면 지속적으로 상태이상 등 덱 활용을 방해하는 카드들을 필터링할 수 있기에 효과가 꽤 좋은 카드.','Foresight.JPG'),
(null,'여류','1','방어도(5→7)','특별','Wather','파워','턴 종료 시 명상 중이라면 방어도를 5 얻습니다.','획득 방어도만 놓고 보면 나쁘지 않은 카드이나, 와쳐의 정수인 진노명상덱에서는 여류보다 마음의 요새를 쓰는 것이 효율이 좋고, 대체재로 쓰려해도 반드시 명상 상태에서 턴을 마치리라는 보장이 없기 때문에 사용이 까다롭다.','Like Water.JPG'),
(null,'열반','1','방어도(3→4)','특별','Wather','파워','예지할 때마다 방어도를 3 얻습니다.','너프 당한 이후 한번에 얻는 방어도가 금속화 수준으로 굴러떨어졌으며, 사실 매 턴마다 예지하는 것조차 선견지명이 없으면 쉽지 않기에 효율이 곤두박질 친다. 너프 이후 활용성이 심히 나빠진 카드.','Nirvana.JPG'),
(null,'전투 찬가','1','선천성 추가','특별','Wather','파워','매 턴 시작시 엄벌을 1 장 얻습니다.','1코스트 12딜을 엄벌을 매 턴마다 수급시켜주는 고효율 카드. 단, 최대한의 이득을 보려면 강화를 해서 선천성을 부여하는 편이 좋다.','Battle Hymn.JPG'),
(null,'추월','1','비용(1→0)','특별','Wather','파워','진노할 때마다 카드를 2 장 뽑습니다.','와쳐의 고질적인 문제인 드로우를 대폭 보완해주는 사기카드. 조건부 드로우지만, 진노는 기본 카드인 분화를 비롯해서 카드풀이 넓으므로 문제가 없다. 강화시 0코스트인 점도 강력. 다만 진노 경지에 진입할 때 드로우이니만큼 이탈/명상 카드 비율은 꾸준히 신경써줘야 한다.','Rushdown.JPG'),
(null,'학습','2','비용(2→1)','특별','Wather','파워','내 턴 종료 시 뽑을 카드 더미에 통찰을 섞어넣습니다.','통찰을 생성하는 다른 카드와 달리 파워카드라서 한번 걸어 놓으면 점차 드로우 이득을 보게 된다. 다만 효과 자체가 매 턴 드로우 한장씩 이득이라 기계학습과 유사한데, 비용도 더 비싸고 속도도 더 느려서 썩 좋지는 않은 파워. 통찰을 섞어 넣는 것은 미리 본 예지를 흐트려 놓는 부가적인 효과가 있는데 이게 매 턴 종료마다 발동하는 것도 작은 단점이다. 일반적으로는 천신의 현상을 사용할 때나 쓸만한 카드.','Study.JPG'),
(null,'지배','1','선천성 추가','희귀','Wather','파워','카드가 보존될 때마다 그 카드의 비용을 이번 전투 동안 1 낮춥니다.','와쳐는 보존 카드를 많이 얻는 편이므로 코스트 이득을 보기 쉽다. 특히 보존이 달려있지 않은 카드들도 보존시켜주는 사색과 시너지는 발군. 다만 거듭된 조정으로 보존카드들의 밸류 자체가 애매해진데다 턴을 낭비한다라는 문제 탓에 계륵이라 애초에 대놓고 쓰라고 만든 보존 카드들보다 사색이 더 효율이 높다.','Establishment.JPG'),
(null,'헌신','1','만트라(2→3)','희귀','Wather','파워','내 턴 시작 시 만트라를 2 얻습니다.','만트라 덱 핵심 카드 중 하나. 단독으로 집기 껄끄러운 부복, 기도와 달리 단독으로 집기 편해 인기가 많다. 단, 헌신 한 장만으로 만트라를 쌓는 것은 강화가 안 되어 있을 경우 5턴, 강화가 되어 있을 경우 4턴이나 걸리기 때문에 다른 만트라 카드를 얻어두는게 좋다. 정석적인 활용법은 숭배와 연계해서 3턴 강림하거나 다마루까지 있다면 2턴 강림도 가능하다.','Devotion.JPG'),
(null,'현실 지배','1','비용(1→0)','희귀','Wather','파워','전투 중에 카드를 생성할 때마다 그 카드를 강화합니다.','와쳐는 강력한 중립 카드를 생성할 일이 많은데, 현실지배는 그 카드를 강화시켜주는 강력한 파워카드. 덤으로, 닐리의 고서나 말라죽은 가지, 각종 포션 등에서 뽑힌 카드들도 강화시켜준다. 마술 칼날로 생성한 말살검을 강화시킬 경우 말살검이 9→15 피해가 되면서 말 그대로 보스를 말살해버리는 것을 볼 수 있다. 그러나 일찍 나와야 좋음에도 선천성이 없으니 사용하기 까다로워 말라죽은 가지나 닐리의 고서가 없다면 카드 슬롯이나 드로우 측면에서 상당히 아깝다.','Master Reality.JPG'),
(null,'천신의 형상','3','휘발성 제거','희귀','Wather','파워','내 턴 시작 시 1 에너지를 얻습니다. 얻는 에너지는 매 턴 1 만큼 증가합니다.(휘발성)','형상 시리즈 카드. 형상 시리즈답게, 강력한 효과를 지닌 대신 코스트를 3 요구한다. 카드 문구가 좀 애매하게 되어 있는데, 매 턴마다 사용 가능한 에너지가 1씩 증가하는 효과이다.','Deva Form.JPG');
INSERT INTO item (id,name,class,dedicated,effect,flavor_text,image) VALUES
(null,'불타는 혈액','시작유물','Ironclad', '전투 종료 시 체력을 6 회복합니다.', '당신의 몸에 흐르는 피는 꺼지지 않는 분노로 타오르고 있습니다.','Burning Blood.JPG'),
(null,'뱀의 반지','시작유물','Cilent', '첫 턴에만 2장의 카드를 추가로 뽑습니다.', '화석이 된 뱀으로 만들었습니다. 뛰어난 기술을 가진 여사냥꾼을 나타냅니다.','Ring of the Snake.JPG'),
(null,'부서진 핵','시작유물','Defect', '전투 시작 시 전기를 1번 영창합니다.', '첨탑에 있는 자동인형에게 미지의 생명의 힘을 동력으로 공급하는 장치입니다. 부서진 것 같아 보입니다.','Cracked Core.JPG'),
(null,'순수한 물','시작유물','Wather','매 전투 시작시 기적을 손으로 가져옵니다.','고운 모래를 통해 여과되어 불순물이 없습니다.','Pure Water.JPG'),
(null,'고대의 차 세트','일반','All','휴식 장소를 거친 다음 전투의 첫 턴에 2의 에너지를 얻습니다.','밤에 상쾌한 휴식을 즐기기 위해 필요한 것입니다.','Ancient Tea Set.JPG'),
(null,'곤충 박제','일반','All','엘리트 방의 적들이 25% 적은 HP를 지닙니다.','이 곤충은 특히 큰 적을 위축시키는 기운을 뿜어낼 수 있었던 것으로 보입니다.','Preserved insect.JPG'),
(null,'구슬 주머니','일반','All','전투 시작 시 적 전체에게 취약을 1 부여합니다.','도시에서 한 때 인기 있던 장난감입니다. 적의 균형을 무너뜨리기 좋습니다.','Bag of Marbles.JPG'),
(null,'금강저','일반','All','힘을 1 얻은 채로 전투를 시작합니다.','명예로운 전투를 한 전사에게 주어진 장식용 유물입니다.','Vajra.JPG'),
(null,'다마루','일반','Wather','내 턴 시작시 만트라를 1 얻습니다.','작은 북소리가 정신이 번쩍 들게 하고, 앞으로 가는 길을 드러냅니다.','Damaru.JPG'),
(null,'닻','일반','All','전투 시작시 방어도를 10 얻습니다.','이 장신구를 잡고 있으면 당신은 안정감을 느낍니다.','Anchor.JPG'),
(null,'데이터 디스크','일반','Defect','밀집을 1 얻은 채로 전투를 시작합니다.','이 디스크는 새와 뱀의 예전 데이터를 담고 있습니다.','Data Disk.JPG'),
(null,'드림 캐쳐','일반','All','휴식할 때마다 카드 보상을 받습니다.','북쪽에 사는 부족들은 종종 밤에 드림 캐쳐를 사용했으며, 이를 통하여 정신 수양을 할 수 있다고 믿었습니다.','Dream Catcher.JPG'),
(null,'딸기','일반','All','최대 체력 +7.',"'정말 맛있군요! 역병이 돌았던 이래로 이렇게 맛있는 것은 본 적이 없어요.' -랜워드",'Strawberry.JPG'),
(null,'매끄러운 돌','일반','All','민첩을 1 얻은 채로 전투를 시작합니다.','당신은 이토록 매끄럽고 깨끗한 것을 본 적이 없습니다. 틀림없이 이것은 고대의 작품일 것입니다.','Oddly Smooth Stone.JPG'),
(null,'물고기 도자기','일반','All','덱에 카드를 추가할 때마다 9 골드를 획득합니다.','세심하게 색칠된 물고기들은, 행운을 불러온다며 모셔지고 있었습니다.','Ceramic Fish.JPG'),
(null,'미소짓는 가면','일반','All','상인의 카드 제거 비용이 50골드로 고정됩니다.','상인이 사용한 가면입니다. 그는 예비품을 가지고 있는 모양입니다…','Smiling Mask.JPG'),
(null,'백년퍼즐','일반','All','매 전투에서 처음 피해를 입을 때 카드를 3장 뽑습니다.','퍼즐을 푸는 순간 당신의 가슴에 강한 온기가 느껴집니다.','Centennial Puzzle.JPG'),
(null,'병법서','일반','All','한 턴 동안 공격 카드를 사용하지 않으면 다음 턴에 1의 에너지를 추가로 얻습니다.','이 고대의 원고는 과거 시대의 지혜를 담고 있습니다','Art of War.JPG'),
(null,'부적','일반','All','앞으로 얻을 2장의 저주를 막아줍니다.','악령을 막아주는 평범한 부적입니다. 이것은 신성한 에너지를 가지고 있는 것 같습니다.','Omamori.JPG'),
(null,'붉은 해골','일반','Ironclad','체력이 50% 이하일 때 힘을 3 얻습니다.','장식용 도색으로 덮힌 작은 해골입니다.','Red Skull.JPG'),
(null,'숫돌','일반','All','획득 시 공격 카드 2장이 무작위로 강화됩니다.',"'육체는 결코 강철을 능가할 수 없습니다.' -위대한 쿠빌라이",'Whetstone.JPG'),
(null,'스네코의 해골','일반','Silent','중독을 부여할 때마다 중독을 1 추가로 부여합니다.','새 것 같은 스네코의 해골입니다. 신기할 정도로 깨끗하고 매끈하며 왠지 모르게 먼지와 때가 사라집니다.','Snecko Skull.JPG'),
(null,'식권','일반','All','상점 방에 진입할 경우 체력을 15 회복합니다.',"'매번 방문 시 미트볼을 무료로 제공합니다!'",'Meal Ticket.JPG'),
(null,'쌍절곤','일반','All','공격 카드를 10장 사용할 때마다 1의 에너지를 얻습니다.','좋은 훈련 도구입니다. 사용자의 자세를 바로잡고 민첩성을 향상시킵니다.','Nunchaku.JPG'),
(null,'아카베코','일반','All','매 전투마다 처음으로 사용하는 공격카드가 추가로 8의 피해를 줍니다.',"'음머어~'",'Akabeko.JPG'),
(null,'아귀 저금통','일반','All','첨탑을 한 층 오를 때마다 12골드를 얻습니다. 상점에서 어떤 방식으로든 골드를 사용한다면 이후 발동하지 않습니다.','아귀가 주기적으로 사람들을 공격함에도 불구하고, 매우 인기가 좋습니다.','Maw Bank.JPG'),
(null,'염주 팔찌','일반','All','?방에서 더 이상 이벤트가 아닌 전투가 발생하지 않습니다.','알 수 없는 기운으로부터 보호받습니다.','Juzu Bracelet.JPG'),
(null,'오리하르콘','일반','All','턴 종료 시 방어도가 없다면 방어도를 6 얻습니다.','녹색으로 빛나는 근원을 알 수 없는 금속. 파괴가 불가능해 보입니다.','Orichalcum.JPG'),
(null,'위장 크림','일반','All','획득 시 스킬 카드 2장이 무작위로 강화됩니다.','과거에, 아이언클래드들은 전투에 나가기 전 마법의 위장크림을 사용했다고 전해집니다.','War Paint.JPG'),
(null,'작은 상자','일반','All','매 4 번째 방문하는 ? 방은 보물방이 됩니다.',"'훌륭한 시제품이군요.' -건축가",'Tiny Chest.JPG'),
(null,'장난감 비행기','일반','All','포션을 사용할 때마다 체력을 5 회복합니다.','이 장난감은 외로운 모험가에게 최고의 동반자입니다!','Toy Ornithopter.JPG'),
