INSERT INTO hero (id,name,hp,flavor_text,image) VALUES
(null, '아이언클레드', 80, '아이언클래드의 살아남은 병사입니다.악마의 힘을 사용하기 위해 영혼을 팔았습니다.','./image/hero/Ironclad.JPG'),
(null, '사일런트', 70, '안개 지대에서 온 치명적인 사냥꾼입니다.단검과 독으로 적들을 박멸합니다.','./image/hero/Cilent.JPG'),
(null, '디펙트', 75, '자아를 깨달은 전투 자동인형입니다.고대의 기술로 구체를 만들 수 있습니다.','./image/hero/Defect.JPG'),
(null, '와쳐', 72, '첨탑을 "평가"하기 위하여 찾아온 눈먼 수도사입니다.강림의 경지에 이른 고수입니다.','./image/hero/Watcher.JPG');
INSERT INTO card (id,name,cost,reinforcement,class,dedicated,type,effect,comments,image) VALUES
(null,'타격','1','피해(6→9),'일반','Ironclad','공격','피해를 6 줍니다.','아이언클래드의 기본카드','Strike_Ironclad.JPG'),
(null,'강타','2','피해(8→10), 취약(2→3),'일반','Ironclad','공격','피해를 8 줍니다.취약을 2 부여합니다.','아이언클래드의 기본카드. 취약 디버프를 건다.','Bash.JPG'),
(null,'분노','0','피해(6→8)','일반','Ironclad','공격','피해를 6 줍니다.이 카드를 1장 복사해 버린 카드 더미에 섞어 넣습니다.','0코스트 증식카드','Anger.JPG'),
(null,'몸통 박치기','0','비용(1→0)','일반','Ironclad','공격','현재 방어도만큼 피해를 줍니다.','방밀덱의 핵심카드','./image/card/Body Slam.JPG'),
(null,'격돌','0','피해(14→18)','일반','Ironclad','공격','손에 있는 카드가 전부 공격 카드일 때만 사용할 수 있습니다.피해를 14 줍니다.','0코스트 고데미지카드. 제약이 심하다.','Clash.JPG'),
(null,'절단','1','피해(8→11)','일반','Ironclad','공격','적 전체에게 피해를 8 줍니다.','무난한 광역기카드','./image/card/Cleave.JPG'),
(null,'클로스라인','2','피해(12→14), 약화(2→3)','일반','Ironclad','공격','피해를 12 줍니다.약화 2를 부여합니다.','준수한 데미지에 약화가 있는 카드 하지만 코스트가 무거워서 잘 쓰기 힘들다.','Clothesline.JPG'),
(null,'박치기','1','피해(9→12)','일반','Ironclad','공격','피해를 9 줍니다.버린 카드 더미에서 카드 1장을 선택해 뽑을 카드 더미의 맨 위에 놓습니다.','다음턴 설계하기에 좋은 카드. 드로우와 연계해서 확정드로우로 사용가능하다.','Headbutt.JPG'),
(null, '대검','2','힘효과(3→5)', '일반', 'Ironclad', '공격','피해를 14 줍니다.힘의 효과가 3배로 적용됩니다.', '힘이 있을 때 높은 위력을 발휘하는 카드 하지만 코스트가 무겁다.', 'Heavy Blade.JPG'),
(null, '철의 파동','1','피해(5→7), 방어(5→7)', '일반', 'Ironclad', '공격','방어도를 5 얻습니다.피해를 5 줍니다.', '공방을 겸하는 카드', 'Iron Wave.JPG'),
(null, '완벽한 타격','2','타격카드로 증가하는 피해량(2→3)', '일반', 'Ironclad', '공격',"피해를 6 줍니다.보유 중인 카드 중 이름에 '타격'이 포함된 카드 하나당 피해량이 2 증가합니다.", '완벽한 타격덱의 필수카드, 폼멜타격/이중타격/난폭한타격/완벽한타격 등을 많이 집어서 피해량을 증식시키는 카드', 'Perfected Strike.JPG'),
(null, '폼멜 타격','1','피해(9→10), 뽑는카드(1장→2장)', '일반', 'Ironclad', '공격','피해를 9 줍니다.카드를 1장 뽑습니다.', '공격 겸 드로우카드', 'Pommel Strike.JPG'),
(null, '부메랑 칼날','1','반복 횟수(3→4)', '일반', 'Ironclad', '공격','무작위 적들에게 피해를 3 만큼 3번 줍니다.', '힘을 올려주는 카드와 시너지가 나는 카드', 'Sword Boomerang.JPG'),
(null, '천둥','1','피해(4→7)', '일반', 'Ironclad', '공격','적 전체에게 피해를 4 주고 취약 1을 부여합니다.', '광역피해 및 광역디버프. 코스트도 낮다.', 'Thunderclap.JPG'),
(null, '이중 타격','1','피해(5→7)', '일반', 'Ironclad', '공격','피해를 5 만큼 2번 줍니다.', '초반에 차용하기에 좋은 준수한 카드. 힘덱으로 빌드업하기에도 좋다.', 'Twin Strike.JPG'),
(null, '난폭한 타격','1','피해(12→17)', '일반', 'Ironclad', '공격','피해를 12 줍니다.뽑을 카드 더미에 부상 카드를 섞어 넣습니다.', '높은피해량. 하지만 카드더미에 부상이 넣어지는 패널티가 있다.', 'Wild Strike.JPG'),
(null, '피에는 피','4','피해(18→22), 비용(4→3)', '특별', 'Ironclad', '공격','이번 전투에서 체력을 잃을 때마다 이 카드의 비용이 1 감소합니다.피해를 18 줍니다.', '막대한 피해량과 코스트 하지만 전투중에 체력을 잃는 상황은 자주있는 상황이라 생각보다 크게 비용이 부담되지는 않는다.', 'Blood for Blood.JPG'),
(null, '대학살','2','피해(20→28)', '특별', 'Ironclad', '공격','피해를 20 줍니다.(휘발성)', '강력한 피해량. 후반에도 휘발성덕분에 사라지므로 크게 부담되지는 않는다. 코스트가 무겁다.', 'Carnage.JPG'),
(null, '드롭킥','1','피해(5→8)', '특별', 'Ironclad', '공격','피해를 5 줍니다.적이 취약 상태라면 에너지 1개를 얻고 카드를 1장 뽑습니다.', '드롭킥 순환덱의 키 카드.', 'Dropkick.JPG'),
(null, '혈류','1','피해(15→20)', '특별', 'Ironclad', '공격','체력을 2 잃습니다.피해를 15 줍니다.', '강력한 피해량.강화량도 좋지만 피해량이 커서 자해덱 등 상황에 맞게 써야한다.', 'Hemokinesis.JPG'),
(null, '연타','1','공격횟수(4→5)', '특별', 'Ironclad', '피해를 2 만큼 4번 줍니다.(소멸)', '다단히트카드로 힘을 올려주는 카드와 시너지가 나는 카드. 저코스트에 소멸이라 가볍게 쓸만하다.', 'Carnage.JPG'),
(null, '광란','1','증가하는 피해량(5→8)', '특별', 'Ironclad', '공격','피해를 8 줍니다.이 카드를 사용할 때마다 이번 전투 동안 이 카드의 피해량이 5 증가합니다.','덱압축만 한다면 주력으로 사용하기에 나쁘지 않은 카드.초반벨류도 나쁘지 않다.', 'Rampage.JPG'),
(null, '불태우는 일격','2','피해(12+n(n+7)/2)', '특별', 'Ironclad', '공격','피해를 12 줍니다.강화 횟수에 제한이 없습니다.', '강화효율이 기하급수적으로 증가하는 특이한 카드. 초반에 얻는다면 엄청난 효과를 누릴수있다.', 'Searing Blow.JPG'),
(null, '영혼 절단','2','피해(16→22)', '특별', 'Ironclad', '공격','손에 있는 공격 카드를 제외한 카드를 모두 소멸시킵니다.피해를 16 줍니다.','소멸과 공격을 동시에하는 카드. 소멸시너지가 급할때 차용하기 좋은 카드.', 'Sever Soul.JPG'),
(null, '어퍼컷','2','취약(1→2), 약화(1→2)', '특별', 'Ironclad', '공격','피해를 13 줍니다.취약을 1 부여합니다.약화를 1 부여합니다.','공격도 하면서 약화와취약을 한장의 카드로 걸 수 있게해주는 카드. 강화의존도가 높다.', 'Uppercut.JPG'),
(null, '소용돌이','X','피해(5→8)', '특별', 'Ironclad', '공격','적 전체에게 피해를 5 만큼 X번 줍니다.','무난한 광역기. 기본공격력이 매우 낮은 단점이 있다. 여러 조건을 만족시킬수만 있다면 효율이 극대화할 수 있다.', 'Whirlwind.JPG'),
(null, '무모한 돌진','0','피해(7→10)', '특별', 'Ironclad', '공격','피해를 7 줍니다.뽑을 카드 더미에 어지러움 카드를 섞어 넣습니다.','0코스트에 준수한 데미지. 그만큼 어지러움이라는 패널티가 있다.', 'Reckless Charge.JPG'),
(null, '몽둥이질','3','피해(32→42)', '희귀', 'Ironclad', '공격','피해를 32 줍니다.','엄청난 깡딜. 그만큼 코스트가 가장 무겁다. 초반에 집는다면 왠만한 1막과 2막의 엘리트몬스터을 포함한 모든 몬스터들은 잡을 수 있다.하지만 후반에는 가성비가 급격히 안좋아지는 카드다.', 'Bludgeon.JPG'),
(null, '포식','1','피해(10→12), 최대 체력 증가량(3→4)', '희귀', 'Ironclad', '공격','피해를 10 줍니다.치명타라면 최대 체력이 3 증가합니다.(소멸)','초반에 집을수록 효율이 좋은카드. 최대체력을 증가시킬 수 있는 것이 장점. 3막보스인 도누를 포식으로 막타로 잡아야하는 도전과제도 있다.', 'Feed.JPG'),
(null, '지옥불','2','피해(7→10)', '희귀', 'Ironclad', '공격','카드를 전부 소멸시킵니다.그 수만큼 피해를 7 줍니다.(소멸)','덱압축에 좋은 카드. 특히 유물인 말라 죽은 가지와 시너지가 뛰어나다.', 'Fiend Fire.JPG'),
(null, '화형','2','피해(21→28)', '희귀', 'Ironclad', '공격','적 전체에게 피해를 21 줍니다.버린 카드 더미에 화상 카드를 섞어 넣습니다.','높은 광역딜. 화상을 얻는다는 패널티가 존재한다.', 'Immolate.JPG'),
(null, '사신','2','피해(4→5)', '희귀', 'Ironclad', '공격','적 전체에게 피해를 4 줍니다.막히지 않은 피해만큼 체력을 회복합니다.(소멸)','흡혈카드. 체력회복인데 광역딜까지겸하는 중요한카드.', 'Reaper.JPG'),
(null, '수비','1','방어도(5→8)', '일반', 'Ironclad', '스킬','방어도를 5 얻습니다.','기본방어카드.스킬카드라 소멸로 소멸시키기 쉬워서 덱압축우선순위에서 타격보다 낮다.', 'Defend_Ironclad.JPG'),
(null, '전투장비','1','카드1장→패에 있는 모든카드', '일반', 'Ironclad', '스킬','방어도를 5 얻습니다.카드 1장을 전투가 끝날 때까지 강화합니다.','신성카드의 하위호환이지만 일반카드라 구하기쉬운점을 생각하면 집기편하다는 장점이 있다.', 'Armaments.JPG'),
(null, '몸 풀기','0','힘(2 → 4), 잃는 힘 (2→4)', '일반', 'Ironclad', '스킬','힘을 2 얻습니다.턴이 끝날 때 힘을 2 잃습니다.','비용이 0이라서 공격카드와 연계하기가 좋다.인공물을 얻어서 힘을 잃는 디버프를 막을 수도 있다.', 'Flex.JPG'),
(null, '파괴','1','비용(1→0)', '일반', 'Ironclad', '스킬','뽑을 카드 더미 맨 위의 카드를 사용한 뒤 소멸시킵니다.','강화의존도가 높은카드. 무거운덱에서 사용하기 좋다.', 'Havoc.JPG'),
(null, '흘려보내기','1','방어도(8→11)', '일반', 'Ironclad', '스킬','방어도를 8 얻습니다.카드를 1장 뽑습니다.','방어와 드로우를 겸한카드 심지어 기본방어카드보다도 방어도가 더 좋다. 1코스트에 방어도도좋고 드로우까지되는 사기카드 중 하나', 'Shrug It Off.JPG'),
(null, '진정한 끈기','1','방어(7→9), 무작위카드소멸→선택한 카드소멸', '일반', 'Ironclad', '스킬','방어도를 7 얻습니다.무작위 카드를 1장 소멸시킵니다.','랜덤성때문에 강화의존도가 매우 높은편. 하지만 저주나 다양한 디버프카드들을 제거하기에 매우 좋은 카드 중 하나이다.', 'True Grit.JPG'),
(null, '전투의 함성','0','뽑는 카드(1장→2장)', '일반', 'Ironclad', '스킬','카드를 1장 뽑습니다.카드 1장을 뽑을 카드 더미 맨 위에 놓습니다.(소멸)','강화를 해야 드로우손해를 안보는 카드라는점에서 강화의존도가 높다. 패가 꼬였을 때 정비하게 해준다는점에서 좋은카드', 'Warcry.JPG'),
(null, '전투 최면','0','뽑는카드(3장→4장)', '특별', 'Ironclad', '스킬','카드를 3장 뽑습니다.이번 턴 동안 더 이상 카드를 뽑을 수 없습니다.','노코스트의 쓰기편한 드로우카드.', 'Battle Trance.JPG'),
(null, '이도류','1','복사량(1장→2장)', '특별', 'Ironclad', '스킬','공격 또는 파워 카드를 복사합니다.','다양한 응용이 가능한 카드. 여러 카드들과 시너지를 내기에 좋다.', 'Dual Wield.JPG'),
(null, '사혈','0','얻는 코스트(2개→3개)', '특별', 'Ironclad', '스킬','코스트 2개를 얻습니다.체력을 3 잃습니다.','체력과 코스트를 교환하는 카드. 자주 쓰기에는 체력이 없어져서 애매하다.', 'Bloodletting.JPG'),
(null, '불타는 조약','1','뽑는카드(2장→3장)', '특별', 'Ironclad', '스킬','카드를 1장 소멸시킵니다.카드를 2장 뽑습니다.','덱압축과 드로우를 동시에 해주는 카드.강화전에는 드로우이득도 없는데 코스트만 잡아먹어서 집을 때 잘 생각해야한다. 그럼에도 불구하고 소멸덱이나 여러카들과의 시너지도 좋은편이다.', 'Burning Pact.JPG'),
(null, '무장 해제','1','힘 감소량(2→3)', '특별', 'Ironclad', '스킬','적의 힘을 2 감소시킵니다.(소멸)','아이언클래드의 유일한 힘감소카드. 영구적효과라는 점에서 좋다.', 'Disarm.JPG'),
(null, '참호','2','비용(2→1)', '특별', 'Ironclad', '스킬','방어도가 2배로 증가합니다.','방밀덱에 활용되는 카드. 일정조건하에서는 엄청난 밸류를 가지고 있는 카드. 코스트가 줄어든다는 점에서 강화의존도가 매우 높은편이다.', 'Entrench.JPG'),
(null, '화염 장벽','2','방어(12→16), 피해(4→6)', '특별', 'Ironclad', '스킬','방어도를 12 얻습니다.이번 턴에 공격을 받을 때마다 공격한 대상에게 피해를 4 줍니다.','방어와 동시에 다단히트계열 몬스터들에게 효과적인 카드. 특히 심장에서 쓴다면 매우 좋다.', 'Flame Barrier.JPG'),
(null, '유령갑옷','1','방어(10→13)', '특별', 'Ironclad', '스킬','방어도를 10 얻습니다.(휘발성)','코스트대비 높은 방어도.패널티도 낮은편이라 무난히 사용하기 좋다.', 'Ghostly Armor.JPG'),
(null, '지옥검','1','비용(1→0)', '특별', 'Ironclad', '스킬','무작위 공격 카드를 1장 얻습니다.이번 턴 동안 그 카드의 비용이 0이 됩니다.(소멸)','이번턴에만 0인것에 주의해야한다. 초반의 부족한 화력을 보충하는데 좋다.', 'Infernal Blade.JPG'),
(null, '위압','0','약화(1→2)', '특별', 'Ironclad', '스킬','적 전체에게 약화를 1 부여합니다.(소멸)','덱에 전체적으로 방어가 낮을때 차용하기 좋다. 소멸덱이라면 매우 좋지만 아니라면 애매한카드.', 'Intimidate.JPG'),
(null, '난관 극복','1','방어(15→20)', '특별', 'Ironclad', '스킬','부상 카드를 2장 받습니다.방어도를 15 얻습니다.','높은 패널티만큼 높은방어와 낮은 코스트. 부상을 해결할 수만 있다면 매우 좋은카드.', 'Power Through.JPG'),
(null, '격노','0','방어(3→5)', '특별', 'Ironclad', '스킬','이번 턴에 공격 카드를 사용할 때마다 방어도를 3 얻습니다.','노코스트라 매력적인 카드. 1장정도라면 고려해볼만하다.', 'Rage.JPG'),
(null, '기사회생','1','방어(5→7)', '특별', 'Ironclad', '스킬','손에 있는 공격 카드를 제외한 모든 카드를 소멸시키고 그 수만큼 방어도를 5 얻습니다.','매우 좋은 성능의 카드. 저주나 상태이상카드들을 지워주면서 방어까지 얻는데 코스트도 낮은 매우 좋은 카드다.심지어 덱압축효과도 있다.', 'Second Wind.JPG'),
(null, '혈안','0','비용(1→0)', '특별', 'Ironclad', '스킬','코스트 2개를 얻습니다.(소멸)','추가코스트를 제공해주는 좋은카드 고코스트덱이나 드로우가 많은덱과 시너지가 좋다.', 'Seeing Red.JPG'),
(null, '감시병','1','방어(5→8), 얻는 코스트(2→3)', '특별', 'Ironclad', '스킬','방어도를 5 얻습니다.이 카드가 소멸되면, 코스트 2개를 얻습니다.','소멸이없으면 수비카드와 다를것이 없는 카드 하지만 소멸만있다면 충분한 성능을 보장한다.', 'Sentinel.JPG'),
(null, '충격파','2','취약(3→5), 약화(3→5)', '특별', 'Ironclad', '스킬','적 전체에게 약화와 취약을 3 부여합니다.(소멸)','무난하게 강력한 카드. 두개의 디버프가 높은수치로 광역으로 걸 수 있다는 점이 장점.', 'Shockwave.JPG'),
(null, '약점 분석','1','힘(3→4)', '특별', 'Ironclad', '스킬','선택한 적이 공격할 의도가 있다면 힘을 3 얻습니다.','파워카드인 발화보다 힘증가량도 높고 여러번 사용가능하지만 상대가 공격할때만 사용가능하다는 조건때문에 타이밍이 엇갈리면 오히려 드로우만 손해보는 카드.', 'Spot Weakness.JPG'),
(null, '연사','1','다음 공격카드(1장→2장)', '희귀', 'Ironclad', '스킬','이번 턴 동안 다음에 사용하는 공격 카드가 두 번 사용됩니다.','2코스트 이상의 딜카드가 주력인 덱에서 차용하기 좋다. 코스트이득을 많이볼 수 있는 카드. 하지만 여유코스트가 없다면 강화효과를 보기는 어렵다.', 'Double Tap.JPG'),
(null, '발굴','1','비용(1→0)', '희귀', 'Ironclad', '스킬','소멸된 카드를 1장 선택해 가져옵니다.(소멸)','소멸카드는 대부분 1회용이지만 높은 성능을 자랑한다.그렇기 때문에 이 카드는 꽤 좋은 성능을 자랑하고, 소멸덱의 경우 상황에 맞는 카드를 골라서 가져올 수도 있다.', 'Exhume.JPG'),
(null, '무적','2','방어(30→40)', '희귀', 'Ironclad', '스킬','방어도를 30 얻습니다.(소멸)','엄청난 방어도를 자랑하는 카드. 한장만으로 한 턴을 버티게해준다. 참호와 연계되면 두배가 되므로 60 or 80의 방어도를 얻을 수 있다. 이것을 방밀을 쓰면 한턴에 60 or 80딜이라는 괴랄한 수치가 나온다.', 'Impervious.JPG'),
(null, '한계 돌파','1','소멸하지 않음', '희귀', 'Ironclad', '스킬','힘이 2배로 증가합니다.(소멸)','힘 덱의 주요 키카드. 곱연산이라서 매우 빠르게 증가하는게 포인트', 'Limit Break.JPG'),
(null, '제물','0','뽑는카드(3→5)', '희귀', 'Ironclad', '스킬','체력을 6 잃습니다.에너지 2개를 얻습니다.카드를 3장 뽑습니다.(소멸)','아이언클래드의 대표적 사기카드. 코스트와 드로우를 동시에 얻을 수 있는 희대의 사기카드.', 'Offering.JPG'),
(null, '연소','1','피해(5→7)', '특별', 'Ironclad', '파워','턴 종료시 체력을 1 잃고 적 전체에게 피해를 5 줍니다.','매턴 체력이 깎이는 만큼 애매한 카드. 하지만 광역기가 필요한 초반보스 특히 슬라임의 경우 효과가 좋다.', 'Combust.JPG'),
(null, '진화','1','뽑는카드(1장→2장)', '특별', 'Ironclad', '파워','상태이상 카드를 뽑으면 카드를 1장 뽑습니다.','강화하면 상태이상이 드로우카드가 되어버리는 카드. 특히 아이언클래드는 패널티를 가진 카드가 많아서 더더욱 좋다.', 'Evolve.JPG'),
(null, '무감각','1','방어도(3→4)', '특별', 'Ironclad', '파워','카드가 소멸될 때마다 방어도를 3 얻습니다.','대표적인 사기카드. 소멸이많은 아이언클래드에게 방어를 챙기게 해주는 카드', 'Feel No Pain.JPG'),
(null, '불 뿜기','1','피해(6→10)', '특별', 'Ironclad', '파워','상태이상이나 저주 카드를 뽑을 경우 적 전체에게 피해를 6 줍니다.','부상덱에서 쓰일만한 키카드.특히 상태이상에 의존하는 몬스터들에게 카운터카드이다.', 'Fire Breathing.JPG'),
(null, '발화','1','힘(2→3)', '특별', 'Ironclad', '파워','힘을 2 얻습니다.','저코스트에 영구적으로 힘을 올려주는 무난한 카드', 'Inflame.JPG'),
(null, '금속화','1','방어(3→4)', '특별', 'Ironclad', '파워','턴 종료시 방어도를 3 얻습니다.','얻을 수 있는 방어도가 매우 낮아서 그렇게 좋지는 않다. 초반에 체력관리가 안될때는 한장정도는 고려해볼만하다.', 'Metallicize.JPG'),
(null, '파열','1','힘(1→2)', '특별', 'Ironclad', '파워','카드의 효과로 체력을 잃을 때마다, 힘을 1 얻습니다.','자해덱에서 사용할 수 있는 카드. 잔혹성과 연계하면 유사 악마화로 사용가능하다. ', 'Rupture.JPG'),
(null, '어둠의 포옹','2','비용(2→1)', '특별', 'Ironclad', '파워','카드가 소멸될 때마다 1장의 카드를 뽑습니다.','소멸덱 회전의 중요 키카드. 이 카드하나로 사이클이 완성될 수 있는 강력한 카드', 'Dark Embrace.JPG'),
(null, '바리케이드','3','비용(3→2)', '희귀', 'Ironclad', '파워','턴 시작시 방어도가 사라지지 않습니다.','방밀덱의 필수카드. 방어도가 사라지지않고 영구지속되는 카드. 그만큼 코스트가 매우 무거워 잘못쓰면 방어도 쌓을려다 얻어맞고 죽는경우가 많다.', 'Barricade.JPG'),
(null, '광폭화','0','취약(2→1)', '희귀', 'Ironclad', '파워','취약을 2 받습니다.매 턴마다 에너지 1개를 추가로 얻습니다.','코스트를 늘려주는 좋은카드. 강화시 디버프를 줄여주는 효과가 있어서 강화의존도가 높은편이다.', 'Berserk.JPG'),
(null, '잔혹성','0','선천성부여', '희귀', 'Ironclad', '파워','턴 시작 시 체력을 1 잃고 카드를 1장 뽑습니다.','적은리스크로 추가드로우를 보게 해주지만 체력관리가 어렵게된다는 단점이 있다.', 'Brutality.JPG'),
(null, '타락','3','비용(3→2)', '희귀', 'Ironclad', '파워','스킬 카드의 비용이 0이 됩니다.스킬 카드를 사용할 때마다 소멸시킵니다.','소멸덱의 핵심카드. 말라죽은 가지와 엄청난 시너지 효과가 있다. 가지타락덱은 이 게임의 모든 덱에서 손꼽는 사기덱이다.', 'Corruption.JPG'),
(null, '악마의 형상','3','힘(2→3)', '희귀', 'Ironclad', '파워','매 턴 시작시 힘을 2 얻습니다.','호불호가 갈리는 카드. 한번쓰면 가면갈수록 무조건 쎄지는 카드지만 코스트가 너무 무거워서 단점이 큰 편이다.', 'Demon Form.JPG'),
(null, '절대적인 힘','2','피해(5→7)', '희귀', 'Ironclad', '파워','방어도를 얻을 때마다 무작위 적에게 5의 피해를 줍니다.','무거운 코스트대비 낮은 리턴으로 잘 안쓰이지만 방밀덱에서 몸통박치기를 못 찾았을 때 차선으로 사용하기 좋다.', 'Juggernaut.JPG'),
(null, '타격','1','피해(6→9)', '일반', 'Silent', '공격','적 하나에게 6의 피해를 입힙니다.','사일런트의 기본공격카드. 성능이 매우 안좋아서 덱압축해야할 최우선순위이다.', 'Strike_Silent.JPG'),
(null, '무력화','0','피해(3→4), 약화(1→2)', '일반', 'Silent','공격', '적 하나에게 3의 피해를 입히고 약화 1을 부여합니다.','기본카드지만 좋은 성능을 발휘하는 카드.특히 강화효율이 엄청 좋은편이다.', '.JPG'),
(null, '파멸','1','피해(7→10)', '일반', 'Silent', '공격', '적 하나에게 7의 피해를 입히고 해당 적이 중독 상태이면 7의 피해를 한 번 더 입힙니다.','독덱에서 사용하기 좋은 카드. 단기간 극딜을 넣을 수 있는 카드.', '.JPG'),
(null, '단검 분사','1','피해(4→6)', '일반', 'Silent', '공격', '모든 적에게 4의 피해를 2번 입힙니다.','초반에 엄청 유용한 카드. 광역카드. ', '.JPG'),
(null, '단검 투척','1','피해(9→12)', '일반', 'Silent', '공격', '적 하나에게 9의 피해를 입히고 카드를 1장 뽑고 1장 버립니다.','덱순환과 준수한 코스트대비 깡딜.', '.JPG'),
(null, '플라잉 니킥','1','피해(8→11)', '일반', 'Silent', '공격', '적 하나에게 8의 피해를 입히고 다음 턴에 코스트 1개를 얻습니다.','자체 피해량이 낮은편이고 즉시 이득을 주지 않아 미묘한 카드.', '.JPG'),
(null, '독 찌르기','1','피해(6→8), 중독(3→4)', '일반', 'Silent', '공격', '적 하나에게 6의 피해를 입히고 중독 3을 부여합니다.','중독딜까지 포함하면 1코스트카드중에서 손꼽히는 딜을 자랑한다.', '.JPG'),
(null, '전광석화','1','피해(8→12)', '일반', 'Silent', '공격', '적 하나에게 8의 피해를 입히고 카드를 1장 뽑습니다.','준수한 딜과 드로우카드. 하지만 폼멜타격의 하위호환격에 시너지도 보기가 어렵다.', '.JPG'),
(null, '칼질','0','피해(6→9)', '일반', 'Silent', '공격', '적 하나에게 6의 피해를 입힙니다.','노코스트의 낮은 피해. 효율이 낮지만 초반에 딜이 부족한경우 채용하는 경우가 있다.', '.JPG'),
(null, '불의의 일격','1','피해(7→9), 약화(1→2)', '일반', 'Silent', '공격', '적 하나에게 7의 피해를 입히고 약화 1을 부여합니다.','크로스라인의 1코스트버전. 이미 무력화라는 약화카드가 있어서 채용하기가 애매하다.', '.JPG'),
(null, '비열한 타격','2','피해(12→16)', '일반', 'Silent', '공격', '12의 피해를 줍니다. 이번 턴에 버린 카드가 있으면 에너지 2를 얻습니다.','버리기와 연계해 노코스트로 준수한 딜을 줄 수 있다.', '.JPG'),
(null, '총공격','1','피해(10→14)', '특별', 'Silent', '공격', '모든 적에게 10의 피해를 입힙니다. 무작위 카드를 1장 버립니다.','높은 광역딜링. 하지만 카드를 버린다는 패널티에 주의해야한다.', '.JPG'),
(null, '배신','0','피해(11→15)', '특별', 'Silent', '공격', '적 하나에게 11의 피해를 입힙니다. 선천성.(소멸)','높은 딜링에 노코스트라는 강력한 카드. 심지어 소멸이라 패널티도 적다.', '.JPG'),
(null, '교살','2','추가피해(3→5)', '특별', 'Silent', '공격', '적 하나에게 12의 피해를 입힙니다. 이번 턴에 카드를 사용할 때마다 해당 적은 3의 피해를 받습니다.','부족한 딜을 채울 수 있는 카드. 특히 적당히 순환이 되는 미라클덱에서는 원턴킬도 노릴만한 카드.', '.JPG'),
(null, '돌진','2','피해(10→13), 방어(10→13)', '특별', 'Silent', '공격', '적 하나에게 10의 피해를 입힙니다. 방어 10을 얻습니다.','딜과 방어를 동시에하는 카드. 초반에 사용하기가 좋다.', '.JPG'),
(null, '영원한 고통','0','피해(4→6)', '특별', 'Silent', '공격', '적 하나에게 4의 피해를 입힙니다. 이 카드를 뽑을 때마다 이 카드를 복사하여 손에 가져옵니다.(소멸)','0코스트에 준수한 딜카드라 초반에 사용하기에 너무좋다.특히 소멸이라 덱순환에 큰 영향을 끼치지 않기 때문에 여러모로 쓸만하다.', '.JPG'),
(null, '내장 해체','4','피해(7→9)', '특별', 'Silent', '공격', '이번 턴에 카드를 버릴 때마다 비용이 1 감소합니다. 적 하나에게 7의 피해를 3번 입힙니다.','미라클덱에서 딜링기로 채택하는 카드. 제대로 연계된 미라클덱에서는 노코스트로 사용가능하지만 애매하게 완성하면 코스트문제때문에 애매할 수 있다.', '.JPG'),
(null, '마무리','1','피해(6→8)', '특별', 'Silent', '공격', '적 하나에게 이번 턴에 사용한 공격 카드 하나당 6의 피해를 입힙니다.','엄청난 폭딜을 낼 수 있는 카드. 그야말로 마무리하기에 좋은 카드다.', '.JPG'),
(null, '프레췌','1','피해(4→6)', '특별', 'Silent', '공격', '적 하나에게 손에 있는 스킬 카드 하나당 4의 피해를 입힙니다.','미라클덱에서 채택하는 주력 딜링수단. 코스트대비 기대값이 매우 높아서 덱순환 도중에 사용하기가 좋다. 특히 드로우가 많은 미라클덱에서 사용하기가 좋다.', '.JPG'),
(null, '다리 꺾기','1','피해(5→8)', '특별', 'Silent', '공격', '적 하나에게 5의 피해를 입힙니다. 해당 적이 약화 효과에 걸린 상태라면 에너지 1개를 얻고 카드를 1장 뽑습니다.','성능은 무난한 편이지만 무한 순환덱을 만들기에는 소멸로 덱압축을 하지않는 사일런트로는 어렵다. 덱압축을 엄청나게 해야한다.', '.JPG'),
(null, '능숙한 찌르기','0','피해(12→16)', '특별', 'Silent', '공격', '피해를 입을 때마다 비용이 1 상승합니다.','피해를 입을수록 손에서 놀게되는 저주카드가 되어버리는 카드. 초반에는 사용할만하지만 1막을 지나면 지워야 하기때문에 상황을 보면서 집어야 한다.', '.JPG'),
(null, '천적','2','피해(15→20)', '특별', 'Silent', '공격', '적 하나에게 15의 피해를 입힙니다. 다음 턴에 카드를 2장 더 뽑습니다.','다음턴을 대비하는 카드.추가2드로우가 생각보다 얻기 힘든 이득이기때문에 1장정도는 채용할만하다.', '.JPG'),
(null, '벌집','2','피해(3→4)', '특별', 'Silent', '공격', '적 하나에게 3의 피해를 5번 입힙니다.','다단히트 공격.하지만 힘을 펌핑할 수단이 사일런트에겐 없어서 애매한 카드.', '.JPG'),
(null, '꼬챙이','X','피해(7→10)', '특별', 'Silent', '공격', '모든 에너지를 소모합니다. 적 하나에게 7의 피해를 X번 입힙니다.','코스트 자원을 펌핑할 수단이 많아서 생각보다 쓸만한 카드. 특히 유물인 아이스크림과 시너지가 좋다.', '.JPG'),
(null, '죽어 죽어 죽어','1','피해(13→17)', '희귀', 'Silent', '공격', '모든 적에게 13의 피해를 입힙니다.(소멸)','엄청 좋은 광역기. 특히 소멸이라 크게 덱메이킹에 방해가 되지 않는게 장점.', '.JPG'),
(null, '유리칼','1','피해(8→12)', '희귀', 'Silent', '공격', '적 하나에게 8의 피해를 2번 입힙니다. 이 카드를 사용할 때마다 이 전투에서만 피해량이 2만큼 감소합니다.','낮은 코스트에 강력한 폭딜. 디버프는 생각보다 한번의 전투에서 여러번 사용하기 어렵기 때문에 보스전이 아니라면 큰 단점이 되지 않는다.', '.JPG'),
(null, '대단원의 막','0','(50→60)', '희귀', 'Silent', '공격', '뽑을 카드 더미에 카드가 하나도 없을 때만 사용할 수 있습니다. 모든 적에게 50의 피해를 입힙니다.','0코스트에 50피해라는 강력한카드. 하지만 발동시키기 난해한 조건을 가지고 있다.', '.JPG'),
(null, '처분','1','피해(14→18)', '희귀', 'Silent', '공격', '적 하나에게 14의 피해를 입힙니다. 손에 들고 있는 카드 중 공격 카드가 아닌 카드들을 모두 버립니다.','저코스트의 상당한 딜량. 하지만 모두 버린다는 조건이 생각보다 잘 쓰이지 않아 선택하기가 애매한 경우가 많다.', '.JPG'),
(null, '수비','1','방어(5→8)', '일반', 'Silent', '스킬', '방어5를 얻습니다.','발놀림카드때문에 제거 우선순위가 상대적으로 낮지만 초반공격이 매우 약하다는 사일런트의 특성상 오히려 수비를 더 빨리 지우는 경우도 있다.', 'Defend_Silent.JPG'),
(null, '생존자','1','(8→11)', '일반', 'Silent', '스킬', '방어 8을 얻습니다. 카드를 1장 버립니다.','사일런트의 기본카드. 상당히 높은 방어도로 초반을 잘 버티게해준다. 후반에도 버리기카드로 연계가 가능해 잘 안지우는 편이다.', '.JPG'),
(null, '곡예','1','뽑는카드(3장→4장)', '일반', 'Silent', '스킬', '카드를 3장 뽑습니다. 카드를 1장 버립니다.','드로우로 덱순환을 가속하면서 버리기로 추가이득도보는 카드.', '.JPG'),
(null, '공중제비','1','방어(5→8)', '일반', 'Silent', '스킬', '방어 5를 얻습니다. 카드를 2장 뽑습니다.','방어와 드로우를 같이보는 카드. 여러덱에 범용적으로 유용한 카드.', '.JPG'),
(null, '맹독','1','(5→7)', '일반', 'Silent', '스킬', '적 하나에게 중독 5를 부여합니다.','독덱의 기본적이면서 강력한 카드.', '.JPG'),
(null, '튕겨내기','0','(4→7)', '일반', 'Silent', '스킬', '방어 4를 얻습니다.','노코스트로 방어도를 얻을 수 있는 카드.', '.JPG'),
(null, '구르기','1','방어(4→6)', '일반', 'Silent', '스킬', '방어 4를 얻습니다. 다음 턴에 방어 4를 얻습니다.','다음턴에도 방어도를 제공해줘서 민첩의 효과를 두배로 받을 수 있다는 이점이 있고, 흐릿함과 연계시 높은 방어도의 이점을 살릴 수 있다.', '.JPG'),
(null, '압도','1','얻는 코스트(2→3)', '일반', 'Silent','스킬', '다음 턴에 에너지 2개를 추가로 얻습니다.','다음턴을 위한 코스트 펌핑카드.', '.JPG'),
(null, '귀를 찢는 비명','1','힘 감소량(6→8)', '일반', 'Silent', '스킬','이번 턴에만 모든 적의 힘을 6만큼 감소시킵니다.(소멸)','매우 좋은 방어카드. 광역힘감소 디버프이다. 특히 심장에서 쓰면 매우 좋은효과를 얻을 수 있다.', '.JPG'),
(null, '예비','1','뽑는 카드(1장→2장), 버리는 카드(1장→2장)', '일반', 'Silent', '스킬','카드를 1장 뽑습니다. 카드를 1장 버립니다.','강화를 안할때는 예비자체가 패를 차지하기 때문에 손해를 보는 카드라서 무조건 강화가 필수이다.', '.JPG'),
(null, '망토와 단검','1','단도카드(1장→2장)', '일반', 'Silent', '스킬','방어 6을 얻습니다. 단도 카드를 1장 얻습니다.','단도덱의 핵심. 공방 둘다되는 카드.', '.JPG'),
(null, '검무','1','단도(3장→4장)', '일반', 'Silent', '스킬','단도 카드 3장을 손으로 가져옵니다.','단도덱의 핵심카드.', '.JPG'),
(null, '흐릿함','1','방어도(5→8)', '특별', 'Silent', '스킬','방어도를 5 얻습니다. 내 다음 턴까지 방어도를 잃지 않습니다.','유사 바리게이트효과를 얻는다는 점에서 엄청 좋은카드. 특히 1코스트에 얻는부분에서 오히려 더 좋은 부분도 있다.', '.JPG'),
(null, '탄성 플라스크','2','반복횟수(3번→4번)', '특별', 'Silent', '스킬','무작위 적에게 중독 3을 부여합니다. 3번 반복합니다.','단일대상일 때 엄청난 중독을 부여하고, 스네코의 해골 유물과 특히 시너지가 엄청좋다.', '.JPG'),
(null, '계산된 도박','0','소멸제거', '특별', 'Silent', '스킬','손에 있는 카드를 모두 버립니다. 버린 카드 개수만큼 카드를 뽑습니다.(소멸)','덱회전에 큰 도움을 주는 카드. 노코스트에 버리기시너지를 가볍게 내기에도 좋다.', '.JPG'),
(null, '촉매','1','증가량(2배→3배)', '특별', 'Silent', '스킬','적 하나의 중독 수치를 2배로 증가시킵니다.(소멸)','독덱의 필살기카드. 엄청난 포텐셜을 가지고 있다.악몽-촉매로 몇배의 독을 넣을수도 있다.', '.JPG'),
(null, '집중','0','버리는 카드(3장→2장)', '특별', 'Silent', '스킬','카드를 3장 버립니다. 에너지 2개를 얻습니다.','코스트 펌핑카드.하지만 엄청난 수의 카드를 버려야 하기 때문에 상당한 부담이 되는 것이 단점', '.JPG'),
(null, '신경독','2','중독(4→7)', '특별', 'Silent', '스킬','적 전체에게 중독 4, 약화 2을 부여합니다.(소멸)','광역 독과 약화를 부여하여 공방 모두 도움이 된다.', '.JPG'),
(null, '방해','1','비용(1→0)', '특별', 'Silent', '스킬','무작위 스킬 카드 1장을 손으로 가져옵니다. 가져온 카드는 이번 턴에만 비용이 0이 됩니다.(소멸)','운빨카드.', '.JPG'),
(null, '탈출구','0','방어도(3→5)', '특별', 'Silent', '스킬','카드 1장을 뽑습니다. 뽑은 카드가 스킬 카드일 경우 방어도를 3 얻습니다.','드로우 손해없이 방어도도 확률적으로 얻을 수 있는 카드.', '.JPG'),
(null, '다리 걸기','2','약화(2→3), 방어도(11→14)', '특별', 'Silent', '스킬','적 하나에게 약화 2를 부여합니다. 방어도를 11 얻습니다.','약화와 방어도가 동시에 있어 방어에 엄청 좋은 카드.', '.JPG'),
(null, '반사 신경','사용불가','뽑는 카드(2장→3장)', '특별', 'Silent', '스킬','이 카드를 버리면 카드를 1장 뽑습니다.','버리기덱의 핵심카드.', '.JPG'),
(null, '설치','1','비용(1→0)', '특별', 'Silent', '스킬','손에 들고 있는 카드 1장을 덱의 맨 위에 놓습니다. 그 카드는 사용하기 전까지 비용이 0이 됩니다.','고비용의 스킬이나 파워를 0코스트로 사용할 수 있게 해주는 카드. 하지만 실제로는 사용하기가 애매한 카드. 강화가 필수적이라는 점도 애매하다.', '.JPG'),
(null, '공포','1','비용(1→0)', '특별', 'Silent', '스킬','적 하나에게 취약 99를 부여합니다.(소멸)','적 하나에게 죽기 전까지 사실상 영구지속 디버프라는 점에서 엄청 좋은 카드.', '.JPG'),
(null, '전문성','1','카드 개수(6장→7장)', '특별', 'Silent', '스킬','손에 들고 있는 카드가 6장이 될 때까지 카드를 뽑습니다.','조건에 따라 많은 카드를 드로우할 수 있다는 점에서 좋다. 하지만 제한된 코스트때문에 사용이 제한될 수 있다는 단점이 있다.', '.JPG'),
(null, '전략가','비용없음','얻는 코스트(1개→2개)', '특별', 'Silent', '스킬','이 카드를 버리면 코스트 1개를 얻습니다.','사일런트 버리기 덱의 핵심.간단하게 코스트를 펌핑하게 해준다.', '.JPG'),
(null, '시체 폭발','2','중독(6→9)', '희귀', 'Silent', '스킬','적 하나에게 중독 6을 부여하고 해당 적 사망시 그 최대 체력만큼 모든 적에게 피해를 줍니다.','사기 광역기 카드. 특히 체력이 높은데 여러마리 있는 몬스터의 경우 이 카드만 있으면 한 마리만 잡아도 해결이 되는 사기 카드이다.', '.JPG'),
(null, '아드레날린','0','얻는 코스트(1개→2개)', '희귀', 'Silent', '스킬','에너지 1개를 얻습니다. 카드를 2장 뽑습니다.','노페널티로 코스트와 드로우를 모두 얻는 개사기카드', '.JPG'),
(null, '연금','1','비용(1→0)', '희귀', 'Silent', '스킬','무작위 포션 1개를 얻습니다.(소멸)','강화시 노코스트가 되는 점에서 이득을 누적시키기 좋다. 무엇보다 불가침과 병 속의 유령을 얻을 확률이 증가한다는 점에서 충분히 가치가 있다.', '.JPG'),
(null, '불릿 타임','3','비용(3→2)', '희귀', 'Silent', '스킬','이번 턴에 더 이상 카드를 뽑을 수 없습니다.이번 턴에 손에 들고 있는 카드의 비용이 0이 됩니다.','코스트 사기를 칠 수 있게 해주는 유용한 카드. 그냥 쓰면 4장만 활용가능해서 애매한 경우도 있다. 드로우를 많이 해서 사용해야한다.', '.JPG'),
(null, '폭주','1','다음스킬카드(1장→2장)', '희귀', 'Silent', '스킬','이번 턴에 사용하는 다음 스킬 카드를 2번 사용합니다.','범용성 높고 사용하기에따라 엄청나게 사기가 되는 카드. 고효율카드와 연계하면 엄청난 효과를 얻을 수 있다.', '.JPG'),
(null, '도플갱어','X','뽑는 카드(X장→X+1장), 코스트(X개→X+1개)', '희귀', 'Silent', '스킬','모든 에너지를 소모합니다. 다음 턴에 카드를 X장 더 뽑고 에너지를 X개 추가로 얻습니다.(소멸)','희귀카드이지만 쓰레기카드. 사용하면 손해를 보는 신기한 카드', '.JPG'),
(null, '악몽','3','비용(3→2)', '희귀', 'Silent', '스킬','손에 들고 있는 카드 하나를 선택합니다. 다음 턴에 해당 카드를 3장 복사하여 손에 가져옵니다.(소멸)','사일런트 최고의 폭발력을 자랑한다. 무거운 코스트를 가지고 있지만 그만큼 리턴이 확실하다.', '.JPG'),
(null, '환영 살인마','1','비용(1→0)', '희귀', 'Silent', '스킬','다음 턴에 적에게 입히는 피해량이 2배로 증가합니다.','패치전에는 애매했으나 패치후에 1코스트가 된 후로 가볍게 쓰기에도 좋다.', '.JPG'),
(null, '강철의 폭풍','2','(단도→강화된 단도)', '희귀', 'Silent', '스킬','손에 있는 카드를 모두 버립니다. 버린 카드 개수만큼 단도 카드를 얻습니다.','단독으로 쓰기에는 어렵고 시너지를 맞추기에도 어렵다.', '.JPG'),
(null, '불쾌','X','힘 감소량(X→X+1), 약화(X→X+1)', '희귀', 'Silent', '스킬','모든 에너지를 소모합니다. 적 하나의 힘을 X만큼 감소시키고 약화 X를 부여합니다.(소멸)','훌륭한 성능의 방어카드. 힘감소는 전투내내 지속되기 때문에 꽤 강력한 디버프이다.', '.JPG'),
(null, '마름쇠','1','피해(3→5)', '특별', 'Silent', '파워','공격을 받을 때마다 공격자에게 3의 피해를 입힙니다.','방어력은 좋은데 공격수단이 부족할 때 사용하기에 좋다. 특히 심장에서 사용하면 매우 좋다.', '.JPG'),
(null, '발놀림','1','민첩(2→3)', '특별', 'Silent', '파워','민첩 2를 얻습니다.','방어카드의 효율을 크게 올려주는 좋은카드.', '.JPG'),
(null, '무한의 검날','1','선천성 부여', '특별', 'Silent', '파워','매 턴마다 단도 카드 한 장을 얻습니다.','단도를 매 턴마다 얻을 수 있는 파워카드. 검무가 상향되서 애매한 카드가 되었다.같은 코스트로 검무한번으로 단도를 얻을 때 이 카드는 4턴을 기다려야된다.', '.JPG'),
(null, '유독가스','1','중독(2→3)', '특별', 'Silent', '파워','매 턴마다 모든 적에게 중독 2를 부여합니다.','독덱을 위한 카드. 인공물빼기에도 좋다. 강화의존도가 높은편이다.', '.JPG'),
(null, '괜찮은 전략','0','선택하는 카드(최대1장→최대2장)', '특별', 'Silent', '파워','매 턴마다 카드를 1장 보존합니다.','매 턴 패가 늘어나는 효과를 얻을 수 있고 전투설계에도 매우좋다.', '.JPG'),
(null, '정밀','1','증가하는 피해량(4→6)', '특별', 'Silent', '파워','단도 카드의 피해량이 4만큼 증가합니다.','단도덱의 화력을 담당하는 카드. 강화 증가치가 높아 강화의존도가 크다.', '.JPG'),
(null, '능지처참','2','피해(1→2)', '희귀', 'Silent', '파워','카드를 사용할 때마다 모든 적에게 1의 피해를 입힙니다.','사일런트 특성상 카드를 많이 사용하는 만큼 준수한 성능을 가지고 있다. 특히 광역딜이라 더욱 좋다.', '.JPG'),
(null, '잔상','1','선천성부여', '희귀', 'Silent', '파워','카드를 사용할 때마다 방어도를 1 얻습니다.','사일런트에게 안정감을 심어주는 훌륭한 카드. 특히 카드를 많이쓰는 덱에 좋다. 독덱 또한 큰 효율은 아니지만 그럼에도 불구하고 많은 방어도를 얻을 수 있어 좋다.', '.JPG'),
(null, '독 바르기','2','비용(2→1)', '희귀', 'Silent', '파워','적이 공격으로 인해 체력을 잃을 때마다 중독 1을 부여합니다.','단도덱에서 고려해볼만한 카드. 기본코스트가 높아서 강화하지 않으면 사용하기 쉽지않다. 기본성능도 미묘한편', '.JPG'),
(null, '작업도구','1','비용(1→0)', '희귀', 'Silent', '파워','매 턴마다 카드를 1장 뽑고 카드를 1장 버립니다.','단순하게 보면 0드로우지만 자세히보면 버리기 카드 시너지를 내줄 수 있다. 특히 팅샤나 붕대와 같은 유물의 효과를 매 턴 볼 수 있게 해준다.', '.JPG'),
(null, '유령의 형상','3','불가침(2→3)', '희귀', 'Silent', '파워','불가침을 2 얻습니다. 턴 종료시 민첩을 1 잃습니다.','최강의 시간벌이용 카드. 강화의존도가 높은편이다. 인공물과 연계해 아예 디버프를 없애는 방법도 있다. 못 없앴다면 불가침끊기기 전에 죽이는 방법을 써야된다.', '.JPG');
INSERT INTO item (id,name,class,dedicated,effect,flavor_text,image) VALUES
(null,'불타는 혈액','시작유물','Ironclad', '전투 종료 시 체력을 6 회복합니다.', '당신의 몸에 흐르는 피는 꺼지지 않는 분노로 타오르고 있습니다.','Burning Blood.JPG'),
(null,'뱀의 반지','시작유물','Cilent', '첫 턴에만 2장의 카드를 추가로 뽑습니다.', '화석이 된 뱀으로 만들었습니다. 뛰어난 기술을 가진 여사냥꾼을 나타냅니다.','Ring of the Snake.JPG'),
(null,'부서진 핵','시작유물','Defect', '전투 시작 시 전기를 1번 영창합니다.', '첨탑에 있는 자동인형에게 미지의 생명의 힘을 동력으로 공급하는 장치입니다. 부서진 것 같아 보입니다.','Cracked Core.JPG'),
(null,'순수한 물','시작유물','Wather','매 전투 시작시 기적을 손으로 가져옵니다.','고운 모래를 통해 여과되어 불순물이 없습니다.','Pure Water.JPG'),
(null,'고대의 차 세트','일반','All','휴식 장소를 거친 다음 전투의 첫 턴에 2의 에너지를 얻습니다.','밤에 상쾌한 휴식을 즐기기 위해 필요한 것입니다.','Ancient Tea Set.JPG'),
(null,'곤충 박제','일반','All','엘리트 방의 적들이 25% 적은 HP를 지닙니다.','이 곤충은 특히 큰 적을 위축시키는 기운을 뿜어낼 수 있었던 것으로 보입니다.','Preserved insect.JPG'),
(null,'구슬 주머니','일반','All','전투 시작 시 적 전체에게 취약을 1 부여합니다.','도시에서 한 때 인기 있던 장난감입니다. 적의 균형을 무너뜨리기 좋습니다.','Bag of Marbles.JPG'),
(null,'금강저','일반','All','힘을 1 얻은 채로 전투를 시작합니다.','명예로운 전투를 한 전사에게 주어진 장식용 유물입니다.','Vajra.JPG'),
(null,'다마루','일반','Wather','내 턴 시작시 만트라를 1 얻습니다.','작은 북소리가 정신이 번쩍 들게 하고, 앞으로 가는 길을 드러냅니다.','Damaru.JPG'),
(null,'닻','일반','All','전투 시작시 방어도를 10 얻습니다.','이 장신구를 잡고 있으면 당신은 안정감을 느낍니다.','Anchor.JPG'),
(null,'데이터 디스크','일반','Defect','밀집을 1 얻은 채로 전투를 시작합니다.','이 디스크는 새와 뱀의 예전 데이터를 담고 있습니다.','Data Disk.JPG'),
(null,'드림 캐쳐','일반','All','휴식할 때마다 카드 보상을 받습니다.','북쪽에 사는 부족들은 종종 밤에 드림 캐쳐를 사용했으며, 이를 통하여 정신 수양을 할 수 있다고 믿었습니다.','Dream Catcher.JPG'),
(null,'딸기','일반','All','최대 체력 +7.',"'정말 맛있군요! 역병이 돌았던 이래로 이렇게 맛있는 것은 본 적이 없어요.' -랜워드",'Strawberry.JPG'),
(null,'매끄러운 돌','일반','All','민첩을 1 얻은 채로 전투를 시작합니다.','당신은 이토록 매끄럽고 깨끗한 것을 본 적이 없습니다. 틀림없이 이것은 고대의 작품일 것입니다.','Oddly Smooth Stone.JPG'),
(null,'물고기 도자기','일반','All','덱에 카드를 추가할 때마다 9 골드를 획득합니다.','세심하게 색칠된 물고기들은, 행운을 불러온다며 모셔지고 있었습니다.','Ceramic Fish.JPG'),
(null,'미소짓는 가면','일반','All','상인의 카드 제거 비용이 50골드로 고정됩니다.','상인이 사용한 가면입니다. 그는 예비품을 가지고 있는 모양입니다…','Smiling Mask.JPG'),
(null,'백년퍼즐','일반','All','매 전투에서 처음 피해를 입을 때 카드를 3장 뽑습니다.','퍼즐을 푸는 순간 당신의 가슴에 강한 온기가 느껴집니다.','Centennial Puzzle.JPG'),
(null,'병법서','일반','All','한 턴 동안 공격 카드를 사용하지 않으면 다음 턴에 1의 에너지를 추가로 얻습니다.','이 고대의 원고는 과거 시대의 지혜를 담고 있습니다','Art of War.JPG'),
(null,'부적','일반','All','앞으로 얻을 2장의 저주를 막아줍니다.','악령을 막아주는 평범한 부적입니다. 이것은 신성한 에너지를 가지고 있는 것 같습니다.','Omamori.JPG'),
(null,'붉은 해골','일반',,'Ironclad','체력이 50% 이하일 때 힘을 3 얻습니다.','장식용 도색으로 덮힌 작은 해골입니다.','Red Skull.JPG'),
(null,'숫돌','일반','All','획득 시 공격 카드 2장이 무작위로 강화됩니다.',"'육체는 결코 강철을 능가할 수 없습니다.' -위대한 쿠빌라이",'Whetstone.JPG'),
(null,'스네코의 해골','일반','Silent','중독을 부여할 때마다 중독을 1 추가로 부여합니다.','새 것 같은 스네코의 해골입니다. 신기할 정도로 깨끗하고 매끈하며 왠지 모르게 먼지와 때가 사라집니다.','Snecko Skull.JPG'),
(null,'식권','일반','All','상점 방에 진입할 경우 체력을 15 회복합니다.',"'매번 방문 시 미트볼을 무료로 제공합니다!'",'Meal Ticket.JPG'),
(null,'쌍절곤','일반','All','공격 카드를 10장 사용할 때마다 1의 에너지를 얻습니다.','좋은 훈련 도구입니다. 사용자의 자세를 바로잡고 민첩성을 향상시킵니다.','Nunchaku.JPG'),
(null,'아카베코','일반','All','매 전투마다 처음으로 사용하는 공격카드가 추가로 8의 피해를 줍니다.',"'음머어~'",'Akabeko.JPG'),
(null,'아귀 저금통','일반','All','첨탑을 한 층 오를 때마다 12골드를 얻습니다. 상점에서 어떤 방식으로든 골드를 사용한다면 이후 발동하지 않습니다.','아귀가 주기적으로 사람들을 공격함에도 불구하고, 매우 인기가 좋습니다.','Maw Bank.JPG'),
(null,'염주 팔찌','일반','All','?방에서 더 이상 이벤트가 아닌 전투가 발생하지 않습니다.','알 수 없는 기운으로부터 보호받습니다.','Juzu Bracelet.JPG'),
(null,'오리하르콘','일반','All','턴 종료 시 방어도가 없다면 방어도를 6 얻습니다.','녹색으로 빛나는 근원을 알 수 없는 금속. 파괴가 불가능해 보입니다.','Orichalcum.JPG'),
(null,'위장 크림','일반','All','획득 시 스킬 카드 2장이 무작위로 강화됩니다.','과거에, 아이언클래드들은 전투에 나가기 전 마법의 위장크림을 사용했다고 전해집니다.','War Paint.JPG'),
(null,'작은 상자','일반','All','매 4 번째 방문하는 ? 방은 보물방이 됩니다.',"'훌륭한 시제품이군요.' -건축가",'Tiny Chest.JPG'),
(null,'장난감 비행기','일반','All','포션을 사용할 때마다 체력을 5 회복합니다.','이 장난감은 외로운 모험가에게 최고의 동반자입니다!','Toy Ornithopter.JPG'),
(null,'장화','일반','All','막히지 않은 피해를 4 이하로 줄 때마다 피해량을 5로 증가시킵니다.','분위기가 고조될 때마다 장화의 크기가 커집니다.','Boot.JPG'),
(null,'전등','일반','All','전투 시작 후 첫 턴에 1의 에너지를 얻습니다.','들고 있는 사람만 밝혀주는 으스스한 랜턴입니다.','Lantern.JPG'),
(null,'준비된 가방','일반','All','첫 턴에만 2장의 카드를 추가로 뽑습니다.','대형 모험가의 배낭. 많은 주머니와 끈이 있습니다.','Bag of Preparation.JPG'),
(null,'청동 비늘','일반','All','전투 시작시 가시를 3 얻습니다.','수호자의 날카로운 비늘입니다. 사용자를 보호하기 위해서 스스로 재배치합니다.','Bronze Scales.JPG'),
(null,'펜 촉','일반','All','10번째 사용하는 공격 카드의 피해량이 두 배로 증가합니다.','펜 촉을 잡자, 당신은 펜의 이전 소유자가 해왔던 모든 학살을 볼 수 있습니다. 폭력의 역사를.','Pen Nib.JPG'),
(null,'포션 벨트','일반','All','획득시 포션 슬롯을 2개 얻습니다.','이 벨트가 있으면 포션을 더 많이 들고 다닐 수 있어!','Potion Belt.JPG'),
(null,'피가 담긴 병','일반','All','전투 시작 시 체력을 2 회복합니다.','나이가 많은 순혈 흡혈귀의 피가 담긴 유리병','Blood Vial.JPG'),
(null,'행복한 꽃','일반','All','3턴마다 1의 에너지를 얻습니다.','기쁨으로 넘치는 이 식물은 귀족들 사이에서 인기가 있는 신기한 물건입니다.','Happy Flower.JPG'),
(null,'호화로운 베개','일반','All','휴식을 취할 때마다 15의 체력을 추가로 회복합니다.','숙면을 위한 필수품입니다.','Regal Pillow.JPG'),
